INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe DareU EH925L Black Red RGB', 'tai-nghe-dareu-eh925l-black-red-rgb', 25,
        '{"Thương hiệu": "DareU", "Model": "EH925L Black Red RGB", "Loại tai nghe": "Chụp tai (Over-ear)", "Kiểu kết nối": "Có dây", "Chuẩn kết nối": "USB", "Kích thước Driver": "50mm", "Trở kháng tai nghe": "32 Ohm", "Độ nhạy tai nghe": "108dB", "Dải tần số": "20Hz - 20KHz", "Microphone": "Có", "Loại microphone": "Đa hướng (Omni-directional)", "Độ nhạy microphone": "-42dB", "Trở kháng microphone": "2.2 kOhm", "Đèn nền": "RGB", "Chiều dài cáp": "2.4m", "Chất liệu đệm tai": "Da Protein", "Màu sắc": "Đen đỏ", "Tương thích": "PC, Laptop"}',
        'Đánh giá Tai nghe DareU EH925L Black Red RGB\n\nNhiều người dùng thường gặp khó khăn với tai nghe nặng nề và không thoải mái, gây khó chịu trong quá trình sử dụng. Tuy nhiên, với trọng lượng nhẹ và đệm tai êm ái, Tai nghe DareU EH925L Black Red RGB nổi bật với thiết kế thể thao và ánh sáng RGB bắt mắt, mang đến trải nghiệm âm thanh sống động, giúp bạn tận hưởng âm nhạc và game trong thời gian dài mà không lo khó chịu.\n\n**Âm thanh sống động, hiệu ứng 7.1 ấn tượng**\n\nDareU EH925L Black Red RGB mang đến cho bạn trải nghiệm âm thanh sống động, chân thực với driver Φ53mm mạnh mẽ. Hệ thống âm thanh DareU-108B được tối ưu hóa để mang đến âm bass sâu, âm treble rõ nét và âm mid ấm áp, giúp bạn tận hưởng trọn vẹn âm nhạc, game hay phim ảnh.\n\nHiệu ứng 7.1 giả lập tạo cảm giác âm thanh vòm, giúp bạn định vị chính xác vị trí âm thanh trong game, đưa bạn vào thế giới game một cách chân thực nhất.\n\n**Thiết kế thời trang, chất lượng cao**\n\nDareU EH925L Black Red RGB được thiết kế với kiểu dáng over-ear hiện đại, cá tính. Đệm tai da mềm êm ái và headband kim loại chắc chắn mang đến sự thoải mái tối đa cho người sử dụng. Hệ thống đèn RGB rực rỡ tăng thêm vẻ đẹp và cá tính cho tai nghe.\n\n**Kết nối tiện lợi, tương thích đa nền tảng**\n\nDareU EH925L Black Red RGB sử dụng kết nối USB tiện lợi, dễ dàng kết nối với máy tính, laptop, mang đến trải nghiệm âm thanh chất lượng cao và tương thích với mọi hệ điều hành.',
        1, 'https://product.hstatic.net/200000722513/product/925_heiwushi_24-07-12_2560_3389_5f7927994ab345ffa6af37b008d3ed78.png'
    );
