INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe DareU EH469 7.1 RGB Pink', 'tai-nghe-dareu-eh469-7-1-rgb-pink', 25,
        '{"Thương hiệu:": "DareU", "Bảo hành:": "12 tháng", "Series/Model:": "EH469 7.1 RGB Pink", "Màu sắc:": "Hồng", "Kiểu tai nghe:": "Over-ear", "Kiểu kết nối:": "Có dây", "LED:": "Có LED RGB tự thay đổi màu sắc", "Chuẩn kết nối:": "USB type A", "Microphone:": "Có thể điều chỉnh hướng", "Trở kháng:": "32 Ohm ở 1KHz", "Tần số:": "20Hz - 20KHz", "Khả năng cách âm:": "Có", "Chất liệu khung:": "Hợp kim", "Chất liệu đệm tai nghe:": "Da mềm", "Phụ kiện đi kèm:": "Hướng dẫn sử dụng", "Tương thích:": "Windows / MacOS", "Chức năng đặc biệt:": "Giả lập âm thanh 7.1"}',
        'Tai nghe DareU EH469 7.1 RGB Pink là sự kết hợp hoàn hảo giữa hiệu năng âm thanh đỉnh cao, thiết kế thời trang và công nghệ hiện đại, mang đến trải nghiệm giải trí và chơi game sống động. Sở hữu công nghệ âm thanh vòm 7.1 ảo, EH469 tái tạo âm thanh chính xác đến từng chi tiết, giúp bạn dễ dàng định vị đối thủ trong game, cảm nhận trọn vẹn từng cung bậc cảm xúc trong âm nhạc hay phim ảnh. Dải âm bass mạnh mẽ, âm trung và cao trong trẻo, EH469 đáp ứng mọi nhu cầu nghe của người dùng. Điểm nhấn nổi bật của chiếc tai nghe này là hệ thống đèn LED RGB với khả năng tùy chỉnh đa dạng, tạo nên hiệu ứng ánh sáng độc đáo, phù hợp với không gian gaming cá tính. Vẻ ngoài màu hồng pastel nhẹ nhàng, nữ tính, kết hợp với đệm tai giả da cao cấp, siêu mềm mại và phần khung tai nghe có thể điều chỉnh linh hoạt, đảm bảo sự thoải mái tối đa ngay cả khi sử dụng trong thời gian dài. Micro có khả năng chống ồn, thu âm rõ ràng, giúp giao tiếp với đồng đội mượt mà. Kết nối USB tiện lợi, tương thích với nhiều thiết bị. Tai nghe DareU EH469 7.1 RGB Pink không chỉ là một phụ kiện âm thanh, mà còn là tuyên ngôn phong cách cho những ai yêu thích sự trẻ trung, năng động và không ngừng khám phá.',
        1, 'https://product.hstatic.net/200000722513/product/34400_dareu_eh469_pink_1d57e30add8f42eca3eaa006d32481e2_5fea9259b65343acadecc1b76079c8b4.jpg'
    );
