INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe ASUS TUF GAMING H1 Wireless Black(90YH0391-B3UA00)', 'tai-nghe-asus-tuf-gaming-h1-wireless-black-90yh0391-b3ua00', 3,
        '{"Công nghệ âm thanh": "Stereo, Virtual 7.1 (Windows Sonic)", "Hướng âm micro": "Định hướng", "Độ nhạy micro": "-45dB", "Đáp ứng tần số micro": "100Hz - 10kHz", "Tính năng": "Có mic đàm thoại", "Cổng kết nối": "3.5mm", "Thao tác điều khiển": "Nút tắt/mở Micro, Tùy chỉnh âm lượng", "Trở kháng tai nghe": "60 ohm", "Chất liệu bộ truyền âm": "Nam châm neođim", "Trọng lượng": "287 g", "Hãng sản xuất": "ASUS"}',
        'Tai nghe ASUS TUF GAMING H1 Wireless Black (90YH0391-B3UA00) mang đến trải nghiệm âm thanh không dây đỉnh cao cho game thủ, kết hợp sự thoải mái, bền bỉ và hiệu suất âm thanh ấn tượng. Với kết nối không dây 2.4GHz, bạn có thể tự do di chuyển mà không lo vướng víu, đồng thời đảm bảo tín hiệu âm thanh ổn định và độ trễ thấp, cực kỳ quan trọng trong các trận đấu game đòi hỏi phản xạ nhanh.\n\nThiết kế của TUF GAMING H1 tập trung vào sự thoải mái khi đeo trong thời gian dài. Earcup bằng da protein cao cấp bọc đệm mút hoạt tính mang lại cảm giác êm ái, đồng thời cách âm hiệu quả. Phần đệm tai nghe được thiết kế công thái học, ôm sát tai mà không gây áp lực khó chịu. Khung tai nghe làm từ thép không gỉ, đảm bảo độ bền bỉ vượt trội, sẵn sàng đồng hành cùng bạn qua mọi cuộc chinh phạt ảo.\n\nMicrophone của TUF GAMING H1 được chứng nhận Discord và TeamSpeak, mang lại khả năng thu âm rõ ràng, loại bỏ tiếng ồn xung quanh, giúp đồng đội của bạn nghe rõ từng mệnh lệnh. Chất âm được tinh chỉnh đặc biệt cho game, với driver ASUS Essence 50mm, tái tạo âm thanh sống động, chi tiết, từ tiếng bước chân lén lút đến những vụ nổ hoành tráng, cho bạn lợi thế cạnh tranh.\n\nVới thời lượng pin lên đến 15 giờ, bạn có thể thoải mái chiến game suốt cả ngày mà không cần lo lắng về việc sạc pin. Tai nghe còn tích hợp các nút điều khiển tiện lợi ngay trên earcup, cho phép bạn dễ dàng điều chỉnh âm lượng, tắt/bật micro mà không cần rời khỏi trận đấu. Tai nghe ASUS TUF GAMING H1 Wireless Black là sự lựa chọn hoàn hảo cho những game thủ chuyên nghiệp và những người yêu thích trải nghiệm gaming đỉnh cao.',
        1, 'https://product.hstatic.net/200000722513/product/w692_6f9f5f8ddac84d3593c8e5fa17926a3f.png'
    );
