INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe Razer Hammerhead True Wireless HyperSpeed (Playstation)', 'tai-nghe-razer-hammerhead-true-wireless-hyperspeed-playstation', 15,
        '{"Hãng sản xuất:": "Razer", "Bảo hành:": "24 tháng", "Model:": "Hammerhead True Wireless Hyperspeed - Playstation", "Màu:": "Trắng", "Kiểu tai nghe:": "In-ear", "Kiểu kết nối:": "True wireless (không dây hoàn toàn)", "Chuẩn kết nối:": "Bluetooth, Wireless", "Khả năng cách âm:": "Có", "Microphone:": "Có", "Tần số:": "20 Hz - 20000 Hz", "Trở kháng:": "16 Ohm", "Độ nhạy:": "Microphone: -26 dBFS", "Phạm vi kết nối:": "10m", "Thời lượng pin:": "LIGHTING OFF / ANC OFF: Lên đến 32.5 giờ (6.5 giờ tai nghe + 26 giờ sạc từ case). Được kiểm tra trên thiết bị IOS với 50% âm lượng. LIGHTING ON / ANC OFF: Lên đến 27.5 giờ (5.5 giờ tai nghe + 22 giờ sạc từ case). Được kiểm tra trên thiết bị IOS với 50% âm lượng và độ sáng ở chế độ breathing effect. LIGHTING OFF / ANC ON: Lên đến 22.5 giờ (4.5 giờ tai nghe + 18 giờ sạc từ case). Được kiểm tra trên thiết bị ISO với 50% âm lượng và ANC ON. LIGHTING ON / ANC ON: Lên đến 20 giờ (4 giờ tai nghe + 16 giờ từ case). Được kiểm tra trên thiết bị IOS với 50% âm lượng, độ sáng ở chế độ breathing effect và ANC ON.", "Drivers:": "10 mm", "Trọng lượng:": "53 g", "Tương thích:": "PS5, PC, MAC, Điện thoại, máy tính bảng và máy chơi game có kết nối bluetooth.", "Phụ kiện:": "Hộp sạc, Cáp sạc USB-C (chiều dài 0.5m), 6 cặp mút tai bằng silicon (SmoothComfort + SecureSeal S/M/L), 1 cặp mút cao cấp Comply (M)."}',
        'Tai nghe không dây Razer Hammerhead Hyperspeed - Dải tần rộng, pin trâu 32.5 giờ\nNếu bạn đang tìm tai nghe in-ear có âm thanh cao cấp cùng khả năng chống ồn vượt trội thì đừng bỏ qua Razer Hammerhead Hyperspeed. Mẫu tai nghe không dây đến từ nhà Razer này được nhiều người dùng đánh giá cao bởi chất âm sống động và cảm giác dễ chịu khi đeo trong thời gian dài. \n\nDriver 10mm, tích hợp kháng ồn ANC thông minh\nTai nghe không dây Razer Hammerhead Hyperspeed giúp tái tạo những giai điệu một cách nguyên bản và chi tiết nhờ driver 10mm, hỗ trợ tần số 20Hz - 20000Hz. Người dùng không cần phải lo lắng về tiếng ồn xung quanh gây ảnh hưởng khi làm việc hay giải trí bởi sản phẩm tích hợp ANC. Công nghệ này sẽ tự động loại bỏ tiếng ồn không mong muốn để bạn tập trung cao độ vào trò chơi hay công việc đang xử lý. \n\nCông suất, công nghệ âm thanh của tai nghe không dây Razer Hammerhead Hyperspeed\n\nThiết kế Ergonomic vừa vặn, ánh sáng Razer Chroma RGB bắt mắt\nSản phẩm được Razer thiết kế theo phong cách công thái học để duy trì sự dễ chịu ngay cả khi sử dụng trong thời gian liên tục. Tai nghe đi đi kèm bộ 6 mút silicon mềm mại với các kích khác nhau để vừa vặn với mọi đôi tai. Đặc biệt, Razer Hammerhead Hyperspeed được tích hợp đèn LED Razer Chroma RGB rực rỡ, cho phép người dùng tuỳ chỉnh nhiều hiệu ứng ánh sáng khác nhau với 16.8 triệu màu rực rỡ. \n\nThiết kế tai nghe không dây Razer Hammerhead Hyperspeed\n\nMicrophone ENC - Khử ồn môi trường kép\nTai nghe không dây Razer Hammerhead Hyperspeed sử dụng mic ứng dụng công nghệ Environmental Noise Cancellation (ENC). Nhờ vậy, sản phẩm có khả năng phát hiện, nhận dạng và loại bỏ tiếng ồn nền gây nhiễu từ môi trường xung quanh. Từ đó, cải thiện khả năng giao tiếp bằng giọng nói trên tai nghe một cách hiệu quả, đảm bảo mọi cuộc đàm thoại của bạn trở nên rõ nét, chi tiết và chính xác hơn bao giờ hết. \n\nMic đàm thoại trên tai nghe không dây Razer Hammerhead Hyperspeed\n\nPhát nhạc liền mạch với dung lượng pin khủng\nVới Razer Hammerhead Hyperspeed, người dùng sẽ được tận hưởng những danh sách nhạc bất tận nhờ thời lượng pin cực trâu. Tai nghe có thể hoạt độnng lên tới 32.5 tiếng khi không bật chế độ Lighting và ANC (6.5 tiếng tai nghe và 26 tiếng hộp sạc). Khi bật chế độ Lighting + ANC, tổng thời gian hoạt động tối đa của sản phẩm có thể đạt tới là 20 giờ.\n\nThời lượng hoạt động của tai nghe không dây Razer Hammerhead Hyperspeed\n\nKết nối tai nghe True Wireless 2.4GHz tiện dụng, siêu nhạy\nĐiểm cộng lớn trên Razer Hammerhead Hyperspeed nằm ở kết nối không dây ổn định cùng đỗ trễ thấp hơn nhiều so với nhiều dòng tai nghe in-ear trên thị trường hiện nay. Sản phẩm ứng dụng công nghệ Razer HyperSpeed Wireless giúp tai nghe kết nối với PlayStation hay các thiết bị khác nhanh chóng. Tốc độ phản hồi cao đảm bảo âm thanh luôn đồng bộ với khung hình để nâng cao trải nghiệm khi chiến game.',
        1, 'https://product.hstatic.net/200000722513/product/hammerhead_hyperspeed_playstation__2022__render_03_917eb422b45241e28161be182ecc5953.png'
    );
