INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe Corsair HS35 V2 Carbon', 'tai-nghe-corsair-hs35-v2-carbon', 5,
        '{"Thương hiệu:": "Corsair", "Bảo hành:": "24 tháng", "Model:": "Corsair HS35 v2 Carbon", "Màu sắc:": "Đen", "Kiểu tai nghe": "Over-ear", "Kiểu kết nối:": "Có dây", "Microphone:": "Có", "Dây:": "Dài 1,8m", "Drivers:": "50mm", "Tương thích:": "PC, Mac, PlayStation 4/5, Xbox One / Series XS, Nintendo Switch, Thiết bị di động", "Khả năng cách âm:": "Có"}',
        'Đắm chìm vào thế giới game với Tai nghe Corsair HS35 V2 Carbon – sự lựa chọn hoàn hảo cho những game thủ tìm kiếm trải nghiệm âm thanh sống động và sự thoải mái vượt trội. Được thiết kế với gam màu Carbon đen mạnh mẽ và tinh tế, HS35 V2 Carbon không chỉ là một phụ kiện gaming mà còn là một tuyên ngôn phong cách.\n\n**Trải nghiệm âm thanh đỉnh cao:** Trang bị củ loa neodymium 50mm tùy chỉnh, tai nghe mang đến chất âm phong phú, chi tiết với âm trầm sâu lắng và âm cao rõ nét. Mọi tiếng bước chân của đối thủ, tiếng súng nổ vang dội hay những bản nhạc nền hùng tráng đều được tái hiện chân thực, giúp bạn dễ dàng nắm bắt mọi lợi thế trong trận chiến.\n\n**Thoải mái tối ưu cho các phiên chơi game kéo dài:** Với thiết kế nhẹ cùng đệm tai nghe bằng vải mềm mại thoáng khí và băng đô có thể điều chỉnh linh hoạt, HS35 V2 Carbon đảm bảo sự thoải mái tuyệt đối ngay cả khi bạn chơi game hàng giờ liền. Không còn cảm giác khó chịu hay nóng bức, chỉ còn sự tập trung hoàn toàn vào trò chơi.\n\n**Giao tiếp rõ ràng, mạch lạc:** Microphone đa hướng chất lượng cao giúp giọng nói của bạn luôn trong trẻo và rõ ràng, đảm bảo mọi mệnh lệnh chiến thuật hay cuộc trò chuyện cùng đồng đội đều được truyền tải một cách hiệu quả nhất. Micro có thể tháo rời, tiện lợi khi bạn chỉ muốn thưởng thức âm nhạc.\n\n**Độ bền bỉ đáng tin cậy:** Được chế tạo từ vật liệu chất lượng cao, HS35 V2 Carbon sẵn sàng đồng hành cùng bạn qua vô số trận chiến.\n\n**Khả năng tương thích đa nền tảng:** Với jack cắm 3.5mm phổ biến, tai nghe tương thích hoàn hảo với PC, Mac, PlayStation, Xbox, Nintendo Switch và các thiết bị di động, mang đến sự linh hoạt tối đa cho mọi thiết lập chơi game của bạn.\n\nNâng tầm trải nghiệm gaming của bạn với Tai nghe Corsair HS35 V2 Carbon – nơi âm thanh sống động gặp gỡ sự thoải mái đỉnh cao.',
        1, 'https://product.hstatic.net/200000722513/product/hs35_v2_artboard01_aa_80b3106cd5654c1e89d113d784994e3c.jpg'
    );
