INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe Edifier Không dây WH700NB Đen', 'tai-nghe-edifier-khong-day-wh700nb-den', 30,
        '{"Tên sản phẩm": "Tai nghe Edifier Không dây WH700NB Đen", "Thương hiệu": "Edifier", "Model": "WH700NB", "Loại tai nghe": "Chụp tai (Over-ear)", "Kiểu kết nối": "Không dây", "Công nghệ kết nối": "Bluetooth", "Phiên bản Bluetooth": "5.3", "Công nghệ chống ồn": "Chống ồn chủ động (ANC)", "Kích thước Driver": "40mm", "Tần số đáp ứng": "20Hz - 20kHz", "Trở kháng": "32 Ohm", "Độ nhạy": "97dB SPL", "Codec Bluetooth hỗ trợ": "SBC, AAC", "Thời lượng pin (tắt ANC)": "Khoảng 68 giờ", "Thời lượng pin (bật ANC)": "Khoảng 45 giờ", "Thời gian sạc": "Khoảng 1.5 giờ", "Cổng sạc": "USB Type-C", "Micro": "Có", "Tính năng đặc biệt": "Chế độ trong suốt, Kết nối đa điểm, Hỗ trợ ứng dụng Edifier Connect", "Màu sắc": "Đen", "Trọng lượng": "Khoảng 271g"}',
        'Tai nghe Edifier WH700NB với thiết kế chụp tai, nhiều công nghệ âm thanh hỗ trợ mang lại những trải nghiệm âm thanh sống động cho người dùng. Vậy chính xác mẫu tai nghe Edifier WH700NB có gì, có đáng để mua và trải nghiệm không thì cùng tìm hiểu chi tiết sau đây.\n\nÂm thanh chất lượng với trình điều khiển 40mm phủ Titanium\nEdifier WH700NB sở hữu trình điều khiển Dynamic với kích thước 40mm. Với thiết kế này, tai nghẹ mang lại âm thanh cao trong suốt và âm trầm sâu lắng. Cùng với đó, Edifier WH700NB còn được trang bị công nghệ khử ồn chủ động với hiệu suất cao. Nhờ đó người dùng có thể sử dụng thiết bị trong nhiều môi trường từ ở thư viện yên tĩnh đến những nơi ồn ào như đi đường.\n\nÂm thanh tai nghe Edifier WH700NB\n\nMàng chắn titanium không chỉ tăng cường độ bền mà còn cải thiện đáng kể độ chi tiết trong âm thanh, tạo ra một dải tần cân bằng, sống động. Ngoài ra, tai nghe còn trang bị công nghệ ANC, bạn có thể tập trung vào âm nhạc hoặc công việc mà không bị phân tâm bởi âm thanh bên ngoài.\n\nThiết kế chụp tai, trải nghiệm thoải mái\nTai nghe Edifier WH700NB được trang bị một thiết kế dạng chụp tai với đệm tai và headband thân thiện với da người dùng. Cùng với đó chùm tai có thể tùy chỉnh nhờ đó giúp thiết bị có thể sử dụng cho nhiều kích thước đầu. Lớp đệm tai và headband được làm từ vật liệu thân thiện với da, mang đến cảm giác êm ái, dễ chịu ngay cả khi sử dụng trong thời gian dài.\n\nThiết kế dạng over-ear giúp bao trọn tai, không chỉ nâng cao khả năng cách âm mà còn tăng thêm sự thoải mái, tránh gây áp lực lên tai. Bên cạnh đó, điểm nổi bật của tai nghe chính là tính di động vượt trội. Khả năng gập gọn giúp sản phẩm trở nên lý tưởng cho những người thường xuyên di chuyển, dễ dàng cất gọn trong balo hoặc túi xách mà không chiếm nhiều diện tích.\n\nThiết kế tai nghe Edifier WH700NB\n\nĐặc biệt, tai nghe sở hữu thiết kế có thể gập lại nhờ đó người dùng có thể dễ dàng mang theo thiết bị. Cùng với đó, thiết bị có tới 4 lựa chọn màu khác nhau là đen, xanh, xám và kem.\n\nNhận diện giọng nói thông minh, đàm thoại rảnh tay thoải mái\nTai nghe Edifier WH700NB được trang bị thuật toán AI nhờ đó hỗ trợ thu lại được giọng nói một cách rõ nét, kể cả khi người dùng sử dụng thiết bị trong môi trường ồn ào. Nhờ đó người dùng có thể sử dụng thiết bị để đàm thoại một cách tiện lợi. Với sự hỗ trợ của công nghệ khử tiếng ồn bằng mạng nơ-ron sâu (DNN) và tính năng ENC (Environmental Noise Cancellation), tai nghe mang đến chất lượng đàm thoại đáng kinh ngạc. Tai nghe WH700NB sẽ tự động lọc bỏ tiếng ồn xung quanh, giữ lại giọng nói của bạn một cách tự nhiên và rõ ràng.\n\nĐược cải tiến bởi thuật toán AI thông minh, WH700NB không chỉ nhận diện giọng nói của bạn mà còn tối ưu hóa để giảm thiểu các tạp âm không mong muốn. Nhờ vậy, bạn có thể tự tin thực hiện các cuộc gọi rảnh tay hoặc tham gia hội nghị trực tuyến mà không cần lo lắng về chất lượng âm thanh.\n\nCông nghệ  tai nghe Edifier WH700NB\n\nĐặc biệt, Edifier WH700NB còn được trang bị chế độ chơi game. Với chế độ này, người dùng có những trải nghiệm trò chơi với hiệu suất vượt trội cũng như định vị dễ dàng thông qua tín hiệu âm thanh.\n\nPin sử dụng tới 68 giờ ấn tượng\nVề thời lượng sử dụng, Edifier WH700NB sở hữu một viên pin dung lượng lớn. Cụ thể, thiết bị có thể mang lại tới 68 giờ sử dụng khi tắt ANC. Nếu sử dụng ở chế độ ANC được kích hoạt thì thời gian sử dụng có thể tới 45 giờ.\n\nPin tai nghe Edifier WH700NB\n\nNgay cả khi bật ANC, thời lượng phát vẫn duy trì ở mức ấn tượng 45 giờ, giúp bạn tận hưởng âm nhạc và sự tập trung trong mọi tình huống. Chỉ cần sạc trong 10 phút, bạn đã có ngay 8 giờ sử dụng cho những ngày bận rộn hoặc những chuyến đi bất ngờ.\n\nBên cạnh viên pin cho thời gian sử dụng lớn thì thiết bị còn được trang bị sạc nhanh công suất lớn. theo đó, chỉ với 10 phút sạc thiết bị mang lại tới 8 giờ phát nhạc ấn tượng.',
        1, 'https://product.hstatic.net/200000722513/product/7f17aabcdda3afc465fb8718f01bc8df_2dc75e902d9f419c851aae6e50c32fd4.png'
    );
