INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe Edifier True Wireless X2S Trắng', 'tai-nghe-edifier-true-wireless-x2s-trang', 30,
        '{"Hãng sản xuất": "Edifier", "Micro": "Có", "Driver loa": "13mm", "Tính năng": "Chống ồn", "Kết nối": "Không dây", "Bluetooth": "5.3", "Ứng dụng kết nối": "Edifier Connect", "Phạm vi kết nối": "10 m", "Dung lượng pin tai nghe": "5V 200mA", "Dung lượng pin hộp sạc": "5V 1A", "Thời lượng pin tai nghe": "6.5 giờ", "Thời lượng pin hộp sạc": "19.5 giờ", "Cổng sạc": "Type-C", "Thời gian sạc tai nghe": "1.5 giờ", "Thời gian sạc hộp sạc": "2 giờ", "Tính năng khác": "Game mode, chống nước và bụi IP54"}',
        'Đắm chìm vào thế giới âm nhạc không giới hạn với Tai nghe Edifier True Wireless X2S Trắng – sự kết hợp hoàn hảo giữa phong cách, hiệu suất và sự tiện lợi. Khoác lên mình gam màu trắng tinh khôi, X2S không chỉ là một thiết bị âm thanh mà còn là phụ kiện thời trang tinh tế, dễ dàng phối hợp với mọi phong cách. Trải nghiệm chất âm Edifier đặc trưng: mạnh mẽ, rõ nét và sống động. Dù bạn là tín đồ của những bản nhạc dance sôi động hay yêu thích giai điệu du dương của nhạc trữ tình, X2S đều mang đến trải nghiệm nghe nhạc chân thực và lôi cuốn, với âm trầm sâu lắng và dải âm cao trong trẻo. Thiết kế công thái học nhẹ nhàng đảm bảo sự thoải mái tuyệt đối ngay cả khi sử dụng trong thời gian dài, ôm khít tai mà không gây khó chịu. Với công nghệ True Wireless, bạn hoàn toàn tự do di chuyển, tập luyện hay làm việc mà không vướng víu dây cáp. Điểm cộng lớn của Edifier X2S Trắng là thời lượng pin ấn tượng. Tận hưởng hàng giờ nghe nhạc liên tục và dễ dàng sạc lại với hộp sạc nhỏ gọn, cho tổng thời gian sử dụng lên đến cả ngày dài. Kết nối Bluetooth ổn định, độ trễ thấp đảm bảo trải nghiệm đồng bộ hóa hoàn hảo, đặc biệt hữu ích khi xem phim hoặc chơi game. Điều khiển cảm ứng trực quan giúp bạn dễ dàng quản lý cuộc gọi, phát/tạm dừng nhạc hay kích hoạt trợ lý giọng nói chỉ với một chạm nhẹ. Edifier X2S Trắng không chỉ là tai nghe, mà là người bạn đồng hành lý tưởng cho cuộc sống năng động, mang đến âm nhạc chất lượng cao mọi lúc, mọi nơi.',
        1, 'https://product.hstatic.net/200000722513/product/25818-681c9839ec416173cdbb27ebed5f7c25_22db6fb1e8cc4de1818f4fd43ad2c063.png'
    );
