INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe E-Dra EH412 Pro RGB USB 7.1', 'tai-nghe-e-dra-eh412-pro', 23,
        '{"Sản phẩm": "Tai nghe", "Hãng sản xuất": "E-DRA", "Model": "EH412", "Thiết kế": "Chụp tai", "Kết nối": "USB", "Micro": "Có", "Màu sắc": "Đen", "Kích thước micro": "4.0 x 1.5mm", "Độ nhạy micro": "-48±3dB", "Hướng thu": "Đa hướng (Omnidirectional)", "Đường kính loa": "50mm", "Trở kháng": "2.2kΩ", "Dải tần số": "20~2000Hz", "Công suất đầu vào tối đa": "100mW", "Độ nhạy loa (S.P.L)": "98±3dB"}',
        'Tai nghe E-Dra EH412 Pro RGB USB 7.1 là sự kết hợp hoàn hảo giữa hiệu năng âm thanh vòm sống động và thiết kế ấn tượng, mang đến trải nghiệm giải trí và chơi game đỉnh cao. Với công nghệ âm thanh vòm 7.1 ảo, EH412 Pro tái tạo không gian âm thanh 3D chân thực, giúp bạn xác định chính xác vị trí kẻ địch, đắm chìm hoàn toàn vào thế giới game hoặc thưởng thức âm nhạc, phim ảnh với chất lượng âm thanh chi tiết và mạnh mẽ. Màng loa lớn mang lại dải tần âm thanh rộng, âm bass sâu lắng và âm treble trong trẻo, đáp ứng mọi nhu cầu nghe nhạc của người dùng khó tính nhất. Điểm nhấn nổi bật của EH412 Pro chính là hệ thống đèn LED RGB với nhiều chế độ hiệu ứng đa dạng, có thể tùy chỉnh theo sở thích, tạo nên một không gian gaming rực rỡ và cá tính. Thiết kế tai nghe chú trọng đến sự thoải mái khi sử dụng trong thời gian dài. Earcup bọc da mềm mại, ôm trọn vành tai, cách âm hiệu quả, trong khi headband có thể điều chỉnh linh hoạt, phân bổ đều trọng lượng giúp giảm áp lực lên đầu. Microphone có khả năng lọc tiếng ồn, thu âm giọng nói rõ ràng, hỗ trợ đàm thoại tốt trong mọi môi trường. Kết nối USB tiện lợi, tương thích với nhiều hệ điều hành và thiết bị. E-Dra EH412 Pro RGB USB 7.1 không chỉ là một chiếc tai nghe, mà còn là một phụ kiện thời trang, một công cụ đắc lực giúp bạn chinh phục mọi thử thách trong thế giới ảo.',
