INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe E-Dra EH494W White', 'tai-nghe-e-dra-eh494w-white', 23,
        '{"Model": "E-Dra EH494W White", "Micro": "Mic rời", "Hướng bắt âm micro": "Đa hướng", "Độ nhạy mic": "-42dB", "Trở kháng mic": "2.2Ω", "Trang bị củ loa": "50mm", "Phạm vi kết nối": "15 mét", "Độ nhạy loa": "119dB", "Trở kháng": "30Ω", "Pin": "1000mAh", "Kết nối": "2.4GHz / Bluetooth 5.0 / Có dây", "Sạc pin": "Qua cổng Type-C trên tai nghe"}',
        'Tai nghe E-Dra EH494W White là một lựa chọn tuyệt vời cho những ai đang tìm kiếm một chiếc tai nghe không dây với chất lượng âm thanh tốt, thiết kế hiện đại và tính năng tiện lợi. Với gam màu trắng tinh khôi, EH494W mang đến vẻ ngoài sang trọng, trẻ trung, dễ dàng phối hợp với nhiều phong cách khác nhau. Công nghệ kết nối không dây Bluetooth 5.0 đảm bảo đường truyền ổn định, phạm vi kết nối rộng và tiết kiệm năng lượng, cho bạn trải nghiệm âm nhạc liền mạch mà không bị gián đoạn. Chất âm của tai nghe được tinh chỉnh để mang lại sự cân bằng giữa các dải tần, từ tiếng bass sâu lắng, mạnh mẽ đến các nốt treble trong trẻo, chi tiết, phù hợp với nhiều thể loại nhạc khác nhau. Bên cạnh đó, EH494W còn được trang bị micro chất lượng cao, hỗ trợ đàm thoại rõ ràng, giúp bạn dễ dàng nghe gọi trong công việc hay khi di chuyển. Thiết kế ear-cup êm ái, ôm sát tai, tạo cảm giác thoải mái ngay cả khi sử dụng trong thời gian dài. Đệm tai được làm từ chất liệu cao cấp, thoáng khí, hạn chế tình trạng bí tai, nóng tai. E-Dra EH494W White còn có dung lượng pin ấn tượng, cho phép bạn nghe nhạc liên tục trong nhiều giờ liền, đáp ứng nhu cầu sử dụng cả ngày. Nút điều khiển tích hợp trên tai nghe cho phép bạn dễ dàng điều chỉnh âm lượng, chuyển bài, nhận cuộc gọi mà không cần thao tác trên điện thoại. Sản phẩm này thực sự là sự kết hợp hoàn hảo giữa hiệu năng, thẩm mỹ và sự tiện dụng, là người bạn đồng hành lý tưởng cho mọi hoạt động giải trí và công việc của bạn.',
