INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe Logitech G435 Lightspeed Wireless - Black', 'tai-nghe-logitech-g435-lightspeed-wireless-black', 13,
        '{"Thương hiệu": "Logitech", "Model": "G435", "Trọng lượng": "165 g", "Kích thước": "163 x 170 x 71 mm", "Driver": "40 mm", "Kết nối": "LIGHTSPEED không dây qua USB 2.0 (Type-A), Bluetooth, tương thích Dolby Atmos, Tempest 3D AudioTech và Windows Sonic Spatial Sound", "Phạm vi kết nối": "10 m", "Pin": "Thời lượng 18 giờ, sạc qua USB-C, dây sạc USB-A sang USB-C", "Phụ kiện đi kèm": "Tai nghe G435, USB-A LIGHTSPEED, dây sạc USB-C sang USB-A, tài liệu hướng dẫn sử dụng", "Độ nhạy tần số": "20 Hz - 20 kHz", "Trở kháng": "45 Ω (bị động)", "Độ nhạy": "83,1 dB SPL/mW"}',
        'Tai nghe Logitech G435 Lightspeed Wireless - Black mang đến trải nghiệm âm thanh không dây đỉnh cao, tự do di chuyển và sự thoải mái vượt trội cho mọi game thủ. Với công nghệ Lightspeed độc quyền của Logitech, tai nghe này cung cấp kết nối không dây cực kỳ nhanh chóng và ổn định, loại bỏ hoàn toàn độ trễ, giúp bạn phản ứng tức thời trong mọi tình huống game. Thiết kế siêu nhẹ chỉ 255g cùng đệm tai mềm mại, thoáng khí, đảm bảo sự thoải mái ngay cả khi sử dụng trong thời gian dài. Màu đen (Black) sang trọng, tinh tế, dễ dàng phối hợp với mọi setup gaming. Loa 40mm mang lại âm thanh sống động, chi tiết, tái tạo chân thực mọi hiệu ứng âm thanh, từ tiếng bước chân kẻ địch đến những pha hành động bùng nổ. Micro tích hợp công nghệ beamforming loại bỏ tiếng ồn xung quanh, thu giọng nói rõ ràng, trong trẻo, giúp bạn giao tiếp hiệu quả với đồng đội. Pin sạc cho thời lượng sử dụng ấn tượng lên đến 18 giờ, thoải mái chiến game cả ngày. Tương thích đa nền tảng: PC, PlayStation, Nintendo Switch, Xbox, thiết bị di động, mang đến sự linh hoạt tối đa. Dễ dàng điều chỉnh âm lượng, tắt/bật mic ngay trên tai nghe. Logitech G435 Lightspeed Wireless - Black không chỉ là một chiếc tai nghe, mà còn là người bạn đồng hành đắc lực, nâng tầm trải nghiệm gaming của bạn lên một tầm cao mới.',
        1, 'https://product.hstatic.net/200000722513/product/tech-g435-lightspeed-wireless-black-1_d9659ca84d664ca38f9f800184e6ac0a_33fb716cdce04b90a09ad00c2de1672e.jpg'
    );
