INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe Logitech G733 LIGHTSPEED Wireless Black', 'tai-nghe-logitech-g733-lightspeed-wireless-black', 13,
        '{"Thương hiệu:": "Logitech", "Bảo hành:": "24 tháng", "Series/Model:": "G733 Lightspeed Wireless Black", "Màu sắc:": "Đen", "Kiểu tai nghe:": "Over-ear", "Kiểu kết nối:": "Không dây", "LED:": "Có LED RGB", "Chuẩn kết nối:": "Receiver USB type A", "Microphone:": "Có thể tháo rời", "Trở kháng:": "1kHz 32 Ohm", "Tần số:": "20Hz - 20KHz", "Khả năng cách âm:": "Có", "Chất liệu khung:": "Nhựa", "Chất liệu đệm tai nghe:": "Vải thoáng", "Phụ kiện đi kèm:": "Hướng dẫn sử dụng, Receiver USB, Cáp sạc USB type A to USB type C", "Tương thích:": "Windows 7 trở lên / macOS X 10.12 trở lên / PlayStation 4", "Chức năng đặc biệt:": "Công nghệ kết nối không dây Lightspeed độc quyền Logitech"}',
        'Tai nghe Logitech G733 LIGHTSPEED Wireless Black là lựa chọn hoàn hảo cho game thủ yêu cầu hiệu suất cao, sự thoải mái tối đa và thiết kế thời thượng. Với công nghệ không dây LIGHTSPEED tiên tiến, bạn sẽ trải nghiệm âm thanh game sống động, không độ trễ, mang lại lợi thế cạnh tranh vượt trội. Dải tần đáp ứng rộng từ 20 Hz đến 20 kHz cùng driver PRO-G độc quyền cho âm thanh chi tiết, mạnh mẽ, tái hiện chính xác mọi âm thanh trong game, từ tiếng bước chân nhỏ nhất đến những vụ nổ hoành tráng. Microphone GƯNG với công nghệ lọc tiếng ồn BLUE VO!CE, được chứng nhận bởi Discord, đảm bảo giọng nói của bạn luôn rõ ràng, chuyên nghiệp khi giao tiếp với đồng đội. Đệm tai bằng mút hoạt tính memory foam mềm mại, cùng thiết kế headband treo độc đáo, phân bổ trọng lượng đều, giúp bạn đeo tai nghe trong nhiều giờ liền mà không cảm thấy mỏi hay khó chịu. Vỏ tai nghe được làm từ vật liệu cao cấp, nhẹ nhàng nhưng bền bỉ, với tông màu đen huyền bí, tô điểm thêm phong cách cho không gian chơi game của bạn. Pin sạc cho thời lượng sử dụng lên đến 29 giờ, đủ sức chinh phục mọi trận đấu dài hơi. Hỗ trợ kết nối đa nền tảng qua USB Receiver, tương thích với PC, PlayStation và Nintendo Switch. Tùy chỉnh hiệu ứng ánh sáng RGB LIGHTSYNC cá nhân hóa theo sở thích để thể hiện cá tính riêng. Logitech G733 LIGHTSPEED Wireless Black – Trải nghiệm âm thanh đỉnh cao, tự do không dây và phong cách dẫn đầu.',
        1, 'https://product.hstatic.net/200000722513/product/ch-g733-lightspeed-wireless-black-666_2eb1a71d562e4a6d853a0f086723cbe3_f7f15fa3c25c4d6190c05c6db168fbf7.png'
    );
