INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe Edifier Không dây W820NB Plus Đen', 'tai-nghe-edifier-khong-day-w820nb-plus-den', 30,
        '{"Công nghệ âm thanh": "Chống ồn chủ động ANC", "Thời lượng pin": "49 giờ", "Thời gian sạc": "1.5 giờ", "Cổng sạc": "Type-C - Sạc nhanh", "Kiểu tai nghe": "Over-ear", "Kiểu kết nối": "USB Type-C, Bluetooth 5.2", "Micro": "Có", "Phạm vi kết nối": "10m", "Thao tác điều khiển": "Chuyển bài, điều chỉnh âm lượng, trả lời cuộc gọi, chuyển đổi giữa các chế độ khác nhau"}',
        'Tai nghe Edifier Không dây W820NB Plus Đen mang đến trải nghiệm âm thanh đỉnh cao với công nghệ chống ồn chủ động (ANC) tiên tiến, loại bỏ hiệu quả tiếng ồn xung quanh, cho bạn đắm chìm hoàn toàn vào thế giới âm nhạc, podcast hay cuộc gọi. Màng loa titan 40mm cỡ lớn tái tạo âm thanh chi tiết, mạnh mẽ, với âm trầm sâu lắng và âm cao trong trẻo, đáp ứng mọi thể loại nhạc. Thời lượng pin ấn tượng lên đến 48 giờ (khi tắt ANC) và 33 giờ (khi bật ANC) đảm bảo bạn có thể sử dụng liên tục trong nhiều ngày mà không lo hết pin. Công nghệ sạc nhanh cho phép bạn có thêm 4 giờ nghe nhạc chỉ với 10 phút sạc. Kết nối Bluetooth 5.2 mới nhất mang đến kết nối ổn định, nhanh chóng và tiết kiệm năng lượng. Chế độ độ trễ thấp (Low Latency Mode) cực kỳ hữu ích cho game thủ, đồng bộ hóa âm thanh và hình ảnh mượt mà. Thiết kế over-ear (trùm tai) với đệm tai bằng da protein mềm mại và phần đệm đầu có thể điều chỉnh mang lại sự thoải mái tối đa ngay cả khi đeo trong thời gian dài. Tích hợp micro kép với công nghệ giảm tiếng ồn ENC giúp thu âm giọng nói rõ ràng, giảm thiểu tiếng ồn từ môi trường, đảm bảo chất lượng cuộc gọi thoại tốt nhất. Nút điều khiển vật lý trực quan trên củ tai cho phép bạn dễ dàng điều chỉnh âm lượng, chuyển bài, nhận/từ chối cuộc gọi và kích hoạt chế độ ANC/chế độ xuyên âm (Ambient Sound Mode) để nghe rõ âm thanh xung quanh khi cần thiết. Ứng dụng Edifier Connect cho phép tùy chỉnh EQ, cập nhật firmware và thiết lập các chế độ nghe phù hợp với sở thích cá nhân. Tai nghe có kiểu dáng tối giản, hiện đại với tông màu đen sang trọng, phù hợp với mọi phong cách. Edifier W820NB Plus Đen là sự lựa chọn hoàn hảo cho những ai yêu thích âm nhạc, thường xuyên di chuyển hoặc làm việc trong môi trường ồn ào, cần một thiết bị âm thanh chất lượng cao, tiện lợi và thoải mái.',
        1, 'https://product.hstatic.net/200000722513/product/d355c57c4f4a47b7f109c7f6bd50ee20_772faa967d304c3d8ee29e3a2aff2e21.png'
    );
