INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe Logitech Không dây Zone Vibe 100 Trắng', 'tai-nghe-logitech-khong-day-zone-vibe-100-trang', 13,
        '{"Thương hiệu": "Logitech", "Model": "Zone Vibe 100", "Trọng lượng": "185 g", "Micro": "Mic MEMS kép đa hướng với chùm tia định hướng và DSP, dải tần số 100~8 kHz", "Kết nối": "Bluetooth 5.2, phạm vi không dây 30 m, cáp sạc USB-C 1.5 m", "Pin": "Pin Lithium-ion, thời gian đàm thoại 18 giờ, nghe nhạc 20 giờ", "Loa": "Driver 40 mm, độ nhạy 118±3 dB @1mW/1kHz, dải tần số 100~8 kHz", "Phụ kiện": "Tai nghe không dây, dây sạc USB-A, túi du lịch, hướng dẫn sử dụng", "Phát triển bền vững": "Nhựa chứa 25% vật liệu tái chế, bao bì đạt chứng nhận FSC™, trung hòa carbon"}',
        'Tai nghe Logitech Không dây Zone Vibe 100 Trắng mang đến trải nghiệm âm thanh đỉnh cao kết hợp với thiết kế hiện đại, tinh tế, phù hợp cho cả công việc và giải trí. Với gam màu trắng trang nhã, chiếc tai nghe này không chỉ là một thiết bị âm thanh mà còn là một phụ kiện thời trang, dễ dàng phối hợp với mọi phong cách. Công nghệ không dây tiên tiến cho phép bạn di chuyển tự do, không bị vướng víu bởi dây cáp, với phạm vi kết nối ổn định, đảm bảo âm thanh liền mạch. Chất lượng âm thanh được tối ưu hóa để mang lại âm bass sâu, âm trung rõ ràng và âm cao sáng trong, tái tạo âm nhạc và giọng nói một cách chân thực nhất. Micro tích hợp với khả năng khử tiếng ồn hiệu quả giúp giọng nói của bạn luôn rõ ràng trong các cuộc gọi, ngay cả trong môi trường ồn ào. Tai nghe được thiết kế công thái học với đệm tai mềm mại, êm ái, ôm trọn vành tai, tạo cảm giác thoải mái khi đeo trong thời gian dài. Khung tai nghe nhẹ nhàng, bền bỉ, cùng khả năng điều chỉnh linh hoạt, phù hợp với nhiều kích cỡ đầu khác nhau. Thời lượng pin ấn tượng cho phép bạn sử dụng liên tục trong nhiều giờ, đáp ứng mọi nhu cầu từ nghe nhạc, xem phim đến làm việc trực tuyến. Logitech Zone Vibe 100 Trắng là sự lựa chọn hoàn hảo cho những ai tìm kiếm sự cân bằng giữa hiệu suất âm thanh, tính di động và phong cách thiết kế.',
        1, 'https://product.hstatic.net/200000722513/product/undp6pbz_8d4ad2d5fc32407cbeaaeca5003f22c2.png'
    );
