INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe Edifier Không dây WH700NB Trắng Ngà', 'tai-nghe-edifier-khong-day-wh700nb-trang-nga', 30,
        '{"Công nghệ âm thanh": "Chống ồn chủ động (ANC)", "Thời lượng pin": "Bật ANC: 68 giờ, Tắt ANC: 45 giờ", "Thời gian sạc": "2 giờ", "Cổng sạc": "Type-C, sạc nhanh", "Kiểu tai nghe": "Over-ear", "Kiểu kết nối": "USB Type-C, Bluetooth 5.0", "Micro": "Có", "Phạm vi kết nối": "10m"}',
        'Tai nghe Edifier Không dây WH700NB Trắng Ngà là sự kết hợp tinh tế giữa công nghệ âm thanh tiên tiến và thiết kế thời thượng, mang đến trải nghiệm nghe nhạc đẳng cấp cùng phong cách thanh lịch. Với gam màu trắng ngà sang trọng và cuốn hút, chiếc tai nghe này không chỉ là một thiết bị âm thanh mà còn là một phụ kiện thời trang nổi bật, dễ dàng phối hợp với mọi trang phục và hoàn cảnh.\n\nĐiểm nhấn vượt trội của WH700NB chính là khả năng Khử tiếng ồn chủ động (Active Noise Cancellation - ANC) mạnh mẽ, giúp loại bỏ hiệu quả các tạp âm xung quanh, từ tiếng ồn giao thông ồn ào đến tiếng trò chuyện trong văn phòng, cho phép bạn hoàn toàn đắm chìm vào không gian âm nhạc riêng tư, yên tĩnh tuyệt đối. Công nghệ âm thanh danh tiếng của Edifier đảm bảo chất lượng âm thanh sống động, chi tiết với âm bass trầm ấm, chắc gọn và âm treble trong trẻo, mang đến những giai điệu chân thực và đầy cảm xúc.\n\nKết nối không dây ổn định và mượt mà qua Bluetooth tiên tiến, đảm bảo truyền tải âm thanh liền mạch và nhanh chóng đến mọi thiết bị của bạn. Thiết kế công thái học với đệm tai mềm mại, ôm sát vừa vặn, tạo cảm giác thoải mái tối đa ngay cả khi sử dụng liên tục trong nhiều giờ. Thời lượng pin ấn tượng giúp bạn thoải mái nghe nhạc, làm việc hoặc giải trí suốt cả ngày dài mà không lo gián đoạn. Tai nghe Edifier WH700NB Trắng Ngà chính là lựa chọn hoàn hảo cho những ai tìm kiếm sự cân bằng giữa chất lượng âm thanh đỉnh cao, sự thoải mái vượt trội và một vẻ ngoài đầy phong cách.',
        1, 'https://product.hstatic.net/200000722513/product/e2c01c2ad210abc59313082a20af7e7d_6485690408fc4b199df5129ffd6e59f6.png'
    );
