INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe Logitech G333 Black', 'tai-nghe-logitech-g333', 13,
        '{"Hai màng loa động kép": "5,8 mm + 9,2 mm", "Độ nhạy tần số": "20 Hz - 20 KHz", "Trở kháng": "24 Ôm ±20%", "Micrô ECM 4 mm, độ nhạy": "-42 dB", "Nền tảng tương thích": "PC, mobile, Xbox, PlayStation, Nintendo, v.v.", "Kiểu tai nghe": "In-Ear", "Kiểu kết nối": "Jack 3.5mm / USB Type-C", "Trọng lượng": "19 gram"}',
        'Tai nghe gaming Logitech G333 là một trong những sản phẩm thuộc dự án hợp tác giữa Logitech và tựa game đình đám Liên Minh Huyền Thoại, và sẽ là mẫu tai nghe gaming tuyệt vời cho những game thủ đích thực.\n\nVỏ nhôm cứng cáp sang trọng, nút tai silicone êm ái cho tai khi đeo\nTai nghe Logitech G333 được thiết kế theo dạng tai nghe có dây với khả năng kết nối kép hai chuẩn USB Type-C và jack 3.5mm, giúp bạn có thể sử dụng Logitech G333 cho cả điện thoại lẫn máy tính hoặc laptop chơi game.\n\nLớp vỏ nhôm cứng cáp với logo chữ \"G\" trứ danh của hãng Logitech góp phần làm nên vẻ ngoài sang trọng cho mẫu tai nghe gaming này. Phần dây nối hiện diện các phím điều chỉnh âm lượng và microphone tích hợp sẵn, giúp cho thao tác voice chat trong game thuận tiện và to rõ.\n\nVỏ nhôm cứng cáp sang trọng, nút tai silicone êm ái cho tai khi đeo\n\nTai nghe Logitech mới này mang đến trải nghiệm âm thanh thoải mái nhất cho bạn thông qua kết cấu nút tai silicone êm ái. Đi kèm trong hộp sản phẩm còn có ba kích cỡ nút tai khác nhau để bạn dễ dàng tùy chỉnh theo ý muốn.\n\nTrang bị màng loa kép cho âm thanh sống động\nLogitech G333 là mẫu tai nghe được hãng Logitech tối ưu hóa cho những tựa game đình đám hiện nay. Trang bị hệ thống màng loa động kép bên trong, Logitech G333 có khả năng tái tạo âm thanh không chỉ mạnh mẽ, mà còn chân thực và sống động ở mọi góc. Bạn sẽ không hề bỏ lỡ bất kỳ chi tiết âm thanh nào trong trò chơi với Logitech G333.\n\nTrang bị màng loa kép cho âm thanh sống động\n\nMàng loa kép được Logitech tinh chỉnh cho âm cao, âm trung và âm trầm cân bằng hoàn hảo, giúp tạo nên một hệ thống âm thanh vòm bao phủ xung quanh bạn. Không chỉ phù hợp cho nhu cầu gaming, tai nghe Logitech G333 còn đáp ứng tốt cả nhu cầu nghe nhạc, làm việc lẫn xem phim với chất lượng âm thanh tuyệt hảo.',
        1, 'https://product.hstatic.net/200000722513/product/logitech-g333_e38d46257e63487c9d72d6d10b1722d3_89596760e3e7462bb9ab6b50d7739876.jpg'
    );
