INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe Steelseries Arctis Nova 3', 'tai-nghe-steelseries-arctis-nova-3', 18,
        '{"Kết nối": "Có dây", "Micro": "ClearCast Gen 2, khử tiếng ồn hai chiều", "Trở kháng": "36 Ohm", "Độ nhạy": "Tai nghe: 93 dBSPL, Micro: -38 dBV/Pa", "Tần số": "Tai nghe: 20–22.000 Hz, Micro: 100-10.000 Hz", "Neodymium Drivers": "40mm", "Tương thích": "PC / Mac / Xbox / PS4, PS5 / Switch / Mobile", "Yêu cầu phần mềm": "Windows 8.1 trở lên, Mac OS 10.13 trở lên, Windows 10 trở lên (Bắt buộc đối với tính năng Khử tiếng ồn bằng AI trên PC)"}',
        'Tai nghe Steelseries Arctis Nova 3 là sự kết hợp hoàn hảo giữa hiệu suất âm thanh đỉnh cao, thiết kế thoải mái và tính năng hiện đại, mang đến trải nghiệm nghe nhạc, chơi game và giải trí đa phương tiện tuyệt vời. Với hệ thống âm thanh Nova Acoustic độc quyền của Steelseries, Arctis Nova 3 tái tạo âm thanh chi tiết, sống động và cân bằng, giúp bạn nghe rõ từng bước chân địch, từng nốt nhạc tinh tế hay từng lời thoại quan trọng. Màng loa Neodymium 40mm được tinh chỉnh chuyên nghiệp cho âm trầm mạnh mẽ, âm trung rõ ràng và âm cao sắc nét, tạo nên một không gian âm thanh chân thực và đắm chìm.\n\nThiết kế của Arctis Nova 3 tập trung vào sự thoải mái tối đa cho người dùng, ngay cả trong những phiên sử dụng kéo dài. Earcup bọc đệm da PU mềm mại, thoáng khí, ôm trọn tai mà không gây áp lực khó chịu. Phần headband có thể điều chỉnh linh hoạt, phân bổ trọng lượng đều, giảm thiểu cảm giác nặng nề. Cùng với đó là trọng lượng nhẹ nhàng, giúp tai nghe trở nên gần như vô hình khi đeo.\n\nNgoài ra, Arctis Nova 3 còn tích hợp micro ClearCast Gen 2 có khả năng khử tiếng ồn AI thông minh, đảm bảo giọng nói của bạn luôn rõ ràng, mạch lạc trong mọi cuộc trò chuyện. Hệ thống đèn LED RGB PrismSync 10 vùng trên earcup cho phép tùy chỉnh hiệu ứng ánh sáng theo ý thích, tạo nên phong cách cá nhân độc đáo và làm nổi bật không gian giải trí của bạn. Khả năng tương thích đa nền tảng với cổng USB-C tiện lợi giúp bạn dễ dàng kết nối với PC, PlayStation, Nintendo Switch, Xbox và các thiết bị di động, mang đến sự linh hoạt tối đa.',
        1, 'https://product.hstatic.net/200000722513/product/-tai-nghe-steelseries-arctis-nova-3-1_9815cec074e24304993ab174e6d380ef_b73fc3f7d93948d78c62d38c8ac8ad57.png'
    );
