INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe SteelSeries Arctis Nova 1 White', 'tai-nghe-steelseries-arctis-nova-1-white', 18,
        '{"Kết nối": "Có dây", "Micro": "ClearCast Gen 2 Khử tiếng ồn hai chiều", "Trở kháng": "36 Ohm", "Độ nhạy": "Tai nghe: 93dBSPL, Micro: -38 dBV/Pa", "Tần số": "Tai nghe: 20–22.000 Hz, Micro: 100-10.000Hz", "Drivers": "40mm", "Điều khiển trên tai nghe": "Tăng, giảm âm lượng - Tắt tiếng", "Tương thích": "PC / Mac / PS4 / PS5 / Nintendo Switch / Điện thoại / Xbox", "Yêu cầu phần mềm": "Windows 8.1 trở lên, Mac OS 10.13 trở lên"}',
        'Tai nghe SteelSeries Arctis Nova 1 White không chỉ là một chiếc tai nghe gaming thông thường, mà còn là một tuyên ngôn về phong cách và hiệu suất vượt trội. Khoác lên mình tông màu trắng tinh khôi, Arctis Nova 1 White mang đến vẻ ngoài thanh lịch, hiện đại và cực kỳ cuốn hút, phù hợp với mọi không gian gaming hay làm việc. Với thiết kế siêu nhẹ, đây là lựa chọn lý tưởng cho những phiên chơi game dài mà không gây cảm giác nặng nề hay khó chịu.\n\nĐược trang bị hệ thống âm thanh Nova Acoustic System đột phá, với driver High Fidelity độc quyền, bạn sẽ được đắm chìm hoàn toàn vào thế giới game với chất âm rõ ràng, chi tiết đến kinh ngạc. Từ tiếng bước chân nhỏ nhất của kẻ địch đến những vụ nổ vang dội, mọi thứ đều được tái hiện chân thực, mang lại lợi thế cạnh tranh không nhỏ. Công nghệ âm thanh không gian 360° (360° Spatial Audio) càng làm tăng thêm trải nghiệm nhập vai, giúp bạn định vị chính xác mọi âm thanh trong môi trường ảo.\n\nSự thoải mái là ưu tiên hàng đầu với Arctis Nova 1. Thiết kế nhẹ nhàng cùng hệ thống ComfortMAX độc quyền, bao gồm đệm tai AirWeave thoáng khí và băng đô co giãn bốn điểm có thể điều chỉnh, đảm bảo bạn có thể chơi game hàng giờ liền mà không cảm thấy khó chịu hay nóng bức.\n\nMicrophone ClearCast Gen 2 tích hợp khả năng khử tiếng ồn mạnh mẽ, mang lại giọng nói trong trẻo, tự nhiên, giúp việc giao tiếp với đồng đội luôn rõ ràng và hiệu quả, loại bỏ hoàn toàn tạp âm từ môi trường xung quanh. Thiết kế có thể thu gọn vào earcup khi không sử dụng cũng cực kỳ tiện lợi và gọn gàng.\n\nĐa năng trên mọi nền tảng, Arctis Nova 1 White tương thích hoàn hảo với PC, PlayStation, Switch, Xbox và các thiết bị di động thông qua cổng 3.5mm phổ biến. Đây chắc chắn là lựa chọn lý tưởng cho game thủ đang tìm kiếm sự kết hợp hoàn hảo giữa phong cách, sự thoải mái và hiệu suất âm thanh đỉnh cao trong một sản phẩm.',
        1, 'https://product.hstatic.net/200000722513/product/thumbtainghe-recovered-recovered_7e457f00ed8740afa352ee77b19310ff_fac958d86ad447c7aa65f8d9211906ff.gif'
    );
