INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe SteelSeries Arctis Nova 1', 'tai-nghe-steelseries-arctis-nova-1', 18,
        '{"Kết nối": "Có dây", "Micro": "ClearCast Gen 2, khử tiếng ồn hai chiều", "Trở kháng": "36 Ohm", "Độ nhạy": "Tai nghe: 93 dBSPL; Micro: -38 dBV/Pa", "Tần số": "Tai nghe: 20–22.000 Hz; Micro: 100-10.000 Hz", "Drivers": "40mm", "Điều khiển trên tai nghe": "Tăng, giảm âm lượng, tắt tiếng", "Tương thích": "PC / Mac / PS4 / PS5 / Nintendo Switch / Điện thoại / Xbox", "Yêu cầu phần mềm": "Windows 8.1 trở lên, Mac OS 10.13 trở lên"}',
        'Tai nghe SteelSeries Arctis Nova 1 là một lựa chọn tuyệt vời cho game thủ và người dùng yêu thích âm thanh chất lượng cao. Với thiết kế hiện đại, thoải mái và hiệu năng âm thanh vượt trội, Arctis Nova 1 mang đến trải nghiệm nghe nhìn sống động và chân thực. Hệ thống loa Neodymium đường kính 40mm tái tạo âm thanh chi tiết, rõ ràng, từ những tiếng bước chân tinh tế đến những vụ nổ mạnh mẽ, giúp bạn hoàn toàn đắm chìm vào thế giới game. Micro ClearCast có thể thu vào với khả năng khử tiếng ồn hai chiều đảm bảo giọng nói của bạn luôn rõ ràng trong mọi cuộc gọi hoặc giao tiếp với đồng đội. Phần đệm tai bằng vải AirWeave thoáng khí cùng dây đeo tai nghe dạng Suspension Headband có thể điều chỉnh linh hoạt giúp phân bổ trọng lượng đều, mang lại sự thoải mái tối đa ngay cả khi sử dụng trong thời gian dài. Thiết kế xoay của củ tai cũng giúp bạn dễ dàng đặt tai nghe nghỉ ngơi khi không sử dụng. Arctis Nova 1 tương thích với nhiều nền tảng khác nhau, bao gồm PC, PlayStation, Xbox, Switch và thiết bị di động thông qua cổng kết nối 3.5mm phổ biến. Vỏ ngoài bền bỉ và các vật liệu cao cấp đảm bảo độ bền lâu dài. Đây là chiếc tai nghe lý tưởng cho những ai tìm kiếm sự cân bằng giữa hiệu suất, sự thoải mái và giá trị.',
        1, 'https://product.hstatic.net/200000722513/product/thumbtainghe-recovered_98cd6388269344c8a5a051edc3609aef_cc8120e49835498aae344671b8018378.gif'
    );
