INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe Asus ROG Delta S Animate', 'tai-nghe-asus-rog-delta-s-animate', 3,
        '{"Thương hiệu": "Asus", "Model": "ROG Delta S Animate", "Màu sắc": "Đen", "Kết nối": "Có dây", "Đầu nối": "USB 2.0 Type-C", "Chất liệu trình điều khiển": "Nam châm neodymium", "Kích thước trình điều khiển": "50 mm", "Kiểu tai nghe": "Over-ear", "Trọng lượng": "310 g", "Trở kháng": "32 Ohm", "Tần số": "20 ~ 40000 Hz", "Khả năng cách âm": "Có", "Độ nhạy của microphone": "-40 dB", "Đáp ứng tần số microphone": "100 ~ 10000 Hz", "Microphone khử tiếng ồn AI": "Có", "Kiểu thu âm của microphone": "Định hướng", "Tương thích": "PC, Mac, PlayStation 4, PlayStation 5, Nintendo Switch, iPad Pro (qua USB-C, chỉ âm thanh). Hỗ trợ PC, PS4, PS5 qua adapter USB-C sang USB-A đi kèm", "Hi-Fi DAC": "ESS 9281 Pro", "Hi-Fi Amp": "ESS 9281 Pro", "Khử tiếng ồn chủ động": "Không", "Kênh âm thanh": "Virtual 7.1", "Hệ thống đèn": "Đèn LED một màu", "Aura Sync": "Không", "Có thể gấp": "Có", "Đệm tai bổ sung": "Có", "Chiều dài cáp": "USB-C: 1.5 m, USB-A: 1 m", "Phụ kiện đi kèm": "Micro tháo rời, Hướng dẫn sử dụng, Đệm tai ROG Hybrid, Adapter USB-C sang USB-A"}',
        'Tai nghe Asus ROG Delta S Animate mang đến trải nghiệm âm thanh đỉnh cao và phong cách gaming độc đáo. Với công nghệ ESS 9281 Hi-Fi DAC và ESS Sabre Hi-Fi có bộ khuếch đại, sản phẩm này tái tạo âm thanh chi tiết, sống động, cho phép bạn nghe rõ từng bước chân, tiếng súng trong game, tạo lợi thế cạnh tranh. Driver ASUS Essence 50mm nam châm neodymium mang đến âm bass mạnh mẽ và âm trường rộng rãi, bao trùm mọi giác quan. Điểm nhấn đặc biệt của ROG Delta S Animate chính là màn hình LED AniMe Matrix™ tùy chỉnh được. Bạn có thể hiển thị hoạt ảnh tùy chỉnh, logo cá nhân hoặc hiệu ứng ánh sáng RGB độc đáo ngay trên cúp tai, thể hiện cá tính game thủ. Công nghệ hủy tiếng ồn AI cho micro kép tự động loại bỏ tiếng ồn xung quanh, đảm bảo giọng nói của bạn luôn rõ ràng, sắc nét trong giao tiếp với đồng đội. Thiết kế công thái học với đệm tai bằng da protein và mút hoạt tính mang lại sự thoải mái tối đa ngay cả khi đeo trong thời gian dài. Kết nối USB-C đa nền tảng cho phép bạn sử dụng tai nghe trên PC, Mac, PlayStation, Nintendo Switch và các thiết bị di động khác. Trọng lượng nhẹ chỉ 300g giúp giảm áp lực lên đầu và cổ. Phần mềm Armoury Crate cho phép tùy chỉnh sâu âm thanh, hiệu ứng ánh sáng và các cài đặt khác, mang đến trải nghiệm cá nhân hóa hoàn hảo.',
        1, 'https://product.hstatic.net/200000722513/product/h732_c276333bd82247469df863ff8638e9a2_cd0815db033d4a418be27bb9cf43312b.png'
    );
