INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai Nghe Rapoo không dây VH800 RGB White Orange', 'tai-nghe-rapoo-khong-day-vh800-rgb-white-orange', 29,
        '{"Thương hiệu:": "Rapoo", "Bảo hành:": "24 tháng", "Model:": "Rapoo VH800", "Màu sắc:": "Cam - Trắng", "Kiểu tai nghe:": "Over-ear", "Khả năng cách âm:": "Có", "Kiểu kết nối:": "Có dây / Wireless / Bluetooth", "Đèn LED:": "Có RGB", "Chuẩn kết nối:": "USB 2.0", "Microphone:": "Có", "Chất liệu đệm tai nghe:": "Chất liệu cao cấp", "Tần số:": "20Hz - 20000Hz", "Trở kháng:": "Tai nghe: 32 ohm, Micro: 2200 ohm", "Độ nhạy:": "Tai nghe: 103 ±3dB, Micro: -42 ±3dB", "Trọng lượng:": "345g"}',
        'Giải phóng mọi giác quan và đắm chìm vào thế giới giải trí đỉnh cao với Tai nghe Rapoo không dây VH800 RGB Trắng Cam – kiệt tác công nghệ kết hợp giữa phong cách, hiệu suất và sự thoải mái.\n\nTrải nghiệm sự tự do tuyệt đối với kết nối không dây mạnh mẽ, cho phép bạn di chuyển linh hoạt mà không bị giới hạn bởi dây cáp. VH800 mang đến chất lượng âm thanh vượt trội, với âm trầm sâu lắng, âm trung rõ ràng và âm cao chi tiết, giúp bạn nghe rõ từng tiếng bước chân của đối thủ, cảm nhận từng nhịp điệu của bản nhạc yêu thích một cách sống động nhất.\n\nĐiểm nhấn không thể bỏ qua là hệ thống đèn nền RGB rực rỡ, tùy chỉnh đa sắc màu, biến không gian chơi game của bạn trở thành một sân khấu ánh sáng đầy mê hoặc. Đặc biệt, phối màu Trắng Cam tươi mới và năng động không chỉ làm nổi bật cá tính riêng mà còn mang lại vẻ ngoài cực kỳ hiện đại và cuốn hút.\n\nVới thiết kế công thái học thông minh, đệm tai mềm mại ôm sát và headband điều chỉnh linh hoạt, VH800 đảm bảo sự thoải mái tối đa ngay cả trong những phiên sử dụng kéo dài. Micro chống ồn tích hợp giúp giao tiếp trong game luôn rõ ràng, liền mạch, nâng cao tinh thần đồng đội. Pin dung lượng cao cho phép bạn tận hưởng hàng giờ liền không gián đoạn.\n\nTai nghe Rapoo VH800 RGB Trắng Cam không chỉ là một chiếc tai nghe, mà là một tuyên ngôn về phong cách và hiệu năng. Nâng tầm trải nghiệm của bạn ngay hôm nay!',
        1, 'https://product.hstatic.net/200000722513/product/___-4_8a6e11f5ad214208aec6da016c664b51.png'
    );
