INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe Corsair HS55 Carbon (CA-9011260-AP)', 'tai-nghe-corsair-hs55-carbon', 5,
        '{"Thương hiệu:": "Corsair", "Bảo hành:": "24 Tháng", "Model:": "Corsair HS55 Carbon", "Màu sắc:": "Đen", "Kiểu tai nghe": "Over-ear", "Kiểu kết nối:": "Có dây", "Microphone:": "Có", "Dây:": "Dài 1,8m", "Tần số :": "Tai nghe: 20Hz - 20000Hz, Micro: 100Hz - 10000Hz", "Trở kháng:": "Tai nghe: 32 Ohms @ 1 kHz, Micro: 2.2k Ohms", "Độ nhạy:": "Tai nghe: 114dB (+/-3dB), Micro: -41dB (+/-3dB)", "Drivers:": "50mm", "Tương thích:": "PC, Mac, PlayStation 4/5, Xbox One / Series XS, Nintendo Switch, Thiết bị di động", "Khả năng cách âm:": "Có"}',
        'HS55 Stereo là dòng sản phẩm tai nghe máy tính mới nhất đến từ Corsair, mang đến nhiều cải tiến về công nghệ âm thanh, giúp người dùng có những trải nghiệm tuyệt vời trong các trò chơi yêu thích.\n\n**Thiết kế ấn tượng, sang trọng**\n\nCorsair là hãng sản xuất thiết bị gaming gear chất lượng cao, được người tiêu dùng đánh giá cao về hiệu năng và thiết kế, từ bàn phím, chuột máy tính, tai nghe cho đến ghế gaming. Tai nghe chơi game Corsair HS55 Stereo là một thiết bị ấn tượng mà bất kỳ game thủ nào, dù là nghiệp dư hay chuyên nghiệp, cũng nên sở hữu. Với thiết kế over-ear (tai nghe chụp tai), sản phẩm mang đến âm thanh tốt và khả năng lọc tiếng ồn hiệu quả. Phối màu carbon sang trọng phù hợp với mọi không gian chơi game, làm việc. Đây không chỉ là phụ kiện giúp nâng cao chất lượng giải trí mà còn làm cho dàn PC của bạn thêm phần nổi bật.\n\n**Công nghệ âm thanh neodymium 50mm**\n\nSử dụng công nghệ âm thanh neodymium 50mm, HS55 Stereo cho phép người dùng tùy chỉnh âm thanh thông qua phần mềm hỗ trợ. Với dải tần số 20Hz - 20000Hz, sản phẩm mang đến âm thanh sống động, mạnh mẽ trong mọi trò chơi, nâng cao trải nghiệm cho mọi nhu cầu sử dụng, từ học tập đến giải trí.\n\n**Micro hỗ trợ thu giọng nói tuyệt vời**\n\nĐể người chơi có thể giao tiếp với đồng đội trong mọi trò chơi, HS55 Stereo Carbon được trang bị microphone gắn liền, có thể điều chỉnh qua phần mềm. Microphone này cho phép thu giọng nói rõ ràng, giúp bạn dễ dàng trao đổi thông tin trong mọi tình huống.\n\n**Trọng lượng siêu nhẹ**\n\nCorsair HS55 Carbon là dòng tai nghe siêu nhẹ, chỉ nặng 284g. Phần headband và chụp tai nghe được làm bằng nhựa, bọc chất liệu giả da cùng đệm foam cao cấp, mang đến sự thoải mái khi sử dụng trong thời gian dài.\n\n**Khả năng tương thích đa thiết bị**\n\nKết nối với nhiều nền tảng bao gồm PC, Xbox One, PlayStation, Switch và thiết bị di động thông qua cổng audio 3.5mm. Đi kèm với tai nghe là bộ chuyển đổi, cho phép HS55 hoạt động dễ dàng trên các máy tính cũ.',
        1, 'https://product.hstatic.net/200000722513/product/thumbtainghe-recovered_78f62db1124e4716a12f057d1a73f1c8_54d79267e0374aa8a441c68dc4c2e094.png'
    );
