INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe Corsair HS80 White RGB Wireless', 'tai-nghe-corsair-hs80-white-rgb-wireless', 5,
        '{"Thương hiệu": "Corsair", "Tần số": "Tai nghe: 20Hz - 40kHz, Micro: 100Hz - 10kHz", "Pin": "Thời lượng lên đến 20 tiếng", "Độ nhạy": "Tai nghe: 116dB (+/-3dB), Micro: -40dB (+/-3dB)", "Kết nối": "Không dây Wireless", "Trở kháng": "Tai nghe: 32k Ohms @ 1kHz, Micro: 2.2k Ohms", "Led": "RGB", "Drivers": "50mm", "Công nghệ âm thanh": "Dolby Atmos", "Tương thích": "PC, Mac, PlayStation 4/5"}',
        'Tai nghe HS80 RGB nó mang đến âm thanh cực kỳ rõ nét thông qua trình điều khiển âm thanh neodymium 50mm, được tuỳ chỉnh điều chỉnh bằng Dolby Atmos đắm chìm.\n\n\n\nHS80 RGB sở hữu chất lượng âm thanh vượt trội\nDòng sản phẩm tai nghe HS80 RGB được trang bị driver âm thanh neodymium 50mm cùng với dải tần số lên đến 20Hz - 30kHz, tai nghe Corsair sẽ đem lại những âm thanh rõ nét nhất từ những tiếng bước chân nhỏ nhẹ đến những vụ nổ chấn động.\n\nTNC Store Tai Nghe Corsair HS80 RGB USB White\n\nChất build tuyệt vời cho cảm giác đeo thoải mái\nHai miếng đệm tai của HS80 RGB được làm từ mút hoạt tính, được bọc bằng lớp vải thoáng khí và headband tự co giãn cho cảm giác thoải mái khi đeo tai nghe để làm việc hoặc giải trí trong thời gian dài mà không bị nặng đầu, khó chịu như những chiếc tai nghe Bluetooth khác.\n\nCông nghệ Dolby Atmos\nTai nghe gaming HS80 RGB trang bị công nghệ âm thanh cao cấp Dolby Atmos mang lại sự chính xác cho âm thanh trong các tựa game, cho bạn cảm nhận và xử lý vượt trội trong nhiều trận thi đấu.\n\nThoải mái kết nối\nSở hữu WIRELESS SLIPSTREAM, Corsair HS80 RGB Wireless cung cấp cho bạn một khả năng kết nối với độ trễ cực thấp, siêu nhanh cùng phạm vi kết nối lên tới 60ft và thời gian sử dụng bền bỉ, 20 tiếng. Đây sẽ là chiếc tai nghe wireless gaming mà bạn sẽ muốn sở hữu đấy.\n\nMicro đa hướng tiện lợi\nBạn có thể thao tác một cách dễ dàng với micro đa hướng của Corsair HS80 RGB Wireless, khi có thể gập lên không sử dụng và gạt xuống để bật mic. Cùng với đèn báo tích hợp ngay trên micro giúp bạn biết tình trạng hoạt động của microphone.\n\nTNC Store Tai Nghe Corsair HS80 RGB USB White\n\nNhững tính năng đi kèm\nĐiều tạo nên sự cải tiến của tai nghe Corsair HS80 RGB đó là những tính năng đi kèm, bao gồm:\n\n \n\nĐiều chỉnh dễ dàng: Bạn có thể nhanh chóng điều chỉnh âm lượng và tắt mic ngay trên tai nghe mà không cần tạm ngưng trò chơi.\nKết nối 3 trong 1: Với một bộ thu USB, có thể hỗ trợ 3 thiết bị có WIRELESS SLIPSTREAM.\nRGB rực rỡ: Tùy chỉnh LED RGB trên Corsair HS80 RGB Wireless cùng hòa mình vào bữa tiệc ánh sáng.\nTương thích PS4 và PS5: Tai nghe gaming Corsair HS80 RGB Wireless có thể kết nối với cả những chiếc máy chơi game console PS4 và PS5.',
        1, 'https://product.hstatic.net/200000722513/product/thumbtainghe_63461bb2a1984570a743fe2b315ee032_30bb537abcc24c5ab103da07b50eb4f1.png'
    );
