INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe Corsair Virtuoso RGB Wireless SE Espresso', 'tai-nghe-corsair-virtuoso-rgb-wireless-se-espresso', 5,
        '{"Hãng sản xuất:": "Corsair", "Model:": "Virtuoso RGB Wireless SE Espresso", "Loại tai nghe:": "Wireless", "Chất liệu:": "Khung kim loại, đệm tai nghe xốp Foam", "Màu sắc:": "Espresso", "Chất lượng âm thanh:": "7.1 Surround", "Âm thanh vòm:": "Có", "Kích thước màng loa (mm):": "50", "Tần số đáp ứng (Hz):": "20Hz - 40kHz", "Trở kháng:": "32 Ohms", "Độ nhạy tai nghe:": "109dB (+/-3dB)", "Tương thích:": "PC, PlayStation 5, PS4", "Phần mềm:": "Audio CUE", "Loại microphone:": "Khử ồn 1 chiều", "Tần số microphone:": "100Hz - 10kHz", "Trở kháng microphone:": "2.2k Ohms", "Kích thước:": "160 x 100 x 205 (mm)"}',
        'Tai nghe Corsair Virtuoso RGB Wireless SE Espresso là đỉnh cao của âm thanh không dây, mang đến trải nghiệm chơi game và giải trí tuyệt vời. Với thiết kế sang trọng tông màu Espresso đầy cuốn hút, sản phẩm không chỉ đẹp mắt mà còn sở hữu chất lượng âm thanh đỉnh cao nhờ củ loa neodymium cao cấp kích thước 50mm, tái tạo âm thanh chi tiết, sống động và mạnh mẽ. Công nghệ âm thanh vòm Dolby® Headphone 7.1 mang đến khả năng định vị âm thanh chính xác, giúp bạn phản ứng nhanh nhạy trong mọi tình huống game. Kết nối không dây độ trễ thấp 2.4GHz Slipstream Wireless đảm bảo tín hiệu ổn định, liền mạch, không giật lag, cùng với tùy chọn kết nối có dây qua USB và 3.5mm cho sự linh hoạt tối đa. Micro đa hướng chất lượng phòng thu có thể tháo rời, trang bị đèn LED báo trạng thái, thu âm giọng nói rõ ràng, khử tiếng ồn hiệu quả, giúp giao tiếp với đồng đội trở nên mượt mà. Đệm tai bằng bọt hoạt tính cao cấp bọc da protein mềm mại, cùng với khung tai nghe bằng nhôm nhẹ nhàng, mang lại sự thoải mái tối đa ngay cả khi đeo trong thời gian dài. Hệ thống đèn LED RGB tùy chỉnh với hàng triệu màu sắc trên củ tai và logo Corsair, cho phép bạn thể hiện phong cách cá nhân độc đáo. Pin sạc dung lượng lớn cung cấp thời gian sử dụng lên đến 20 giờ, đáp ứng nhu cầu sử dụng liên tục. Phần mềm iCUE mạnh mẽ cho phép tùy chỉnh EQ, hiệu ứng ánh sáng và nhiều cài đặt âm thanh khác, mang đến trải nghiệm âm thanh hoàn toàn theo ý muốn của bạn. Corsair Virtuoso RGB Wireless SE Espresso - lựa chọn hoàn hảo cho game thủ và người yêu âm nhạc.',
