INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe Corsair HS65 Surround White', 'tai-nghe-corsair-hs65-surround-white', 5,
        '{"Thương hiệu": "Corsair", "Bảo hành": "24 Tháng", "Model": "Corsair HS65 Surround White", "Màu sắc": "Trắng", "Kiểu tai nghe": "Over-ear", "Kiểu kết nối": "Có dây", "Microphone": "Có (không thể tháo rời)", "Âm thanh": "Dolby Audio", "Âm thanh vòm": "Có", "Trọng lượng": "282g", "Tần số": "20Hz - 20 kHz", "Trở kháng": "32k Ohms @ 1 kHz", "Độ nhạy": "114dB (+/-3dB)", "Drivers": "50mm", "Phần mềm": "Audio CUE", "Chiều dài cáp": "1.8m", "Tương thích": "PC, Mac, PlayStation 4/5, Xbox One / Series XS, Nintendo Switch, Mobile devices", "Khả năng cách âm": "Có"}',
        'Corsair HS65 Surround White đặt vào mắt người dùng với là một thiết kế vô cùng trực quan. Cảm giác chắc chắn khi được hoàn thiện với những chi tiết từ kim loại nhưng chiếc tai nghe Corsair vẫn sở hữu cho mình trọng lượng chỉ 282g. Trọng lượng nhẹ kết hợp cùng băng đô với những sợi thoáng khí li ti, cộng thêm phần đệm tai nghe từ mút hoạt tính bọc bên ngoài là lớp giả da, tất cả đều hướng tới trải nghiệm đeo tai nghe thoải mái và tuyệt vời nhất.\n\n- Tai nghe Corsair HS65 Surround White\n\nCon lăn điều chỉnh âm lượng cùng micro trên Corsair HS65 Surround White đặt tại vị trí dễ dàng cho việc thao tác.\n\nNâng cấp trải nghiệm âm thanh\nHướng tới một trải nghiệm nghe đỉnh cao, Corsair đã kết hợp phần driver tai nghe neodymium 50mm cùng công nghệ Dolby Audio 7.1 Surround ngay trên HS65 Surround White. Từ đó, chiếc tai nghe gaming từ Corsair biến thành chiếc chìa khóa đưa bạn những trận chiến, cảm nhận từng tiếng bước chân và tiếng súng hay đắm mình vào những bản nhạc, bộ phim yêu thích.\n\n- Tai nghe Corsair HS65 Surround White\n\nCải thiện chất lượng cuộc gọi \nMicrophone đa hướng trên Corsair HS65 Surround White sẽ giúp bạn thu và ghi lại giọng nói một cách tối đa. Corsair HS65 Surround White được một trong những platform nổi tiếng về mạng xã hội, nhắn tin và gọi với game thủ - Discord, công nhận về chất lượng mọi cuộc gọi khi luôn đem lại âm thanh giao tiếp rõ ràng, nâng cao chất lượng mọi cuộc hội thoại online.\n\n- Tai nghe Corsair HS65 Surround White\n\nNgoài ra, micro trên Corsair HS65 Surround White dễ dàng tắt/bật chỉ thông qua thao tác gạt lên/xuống, cho phép người dùng có thể thao tác song song với mọi công việc đang thực hiện. \n\nThoải mái kết nối\nĐi kèm với Corsair HS65 Surround White là chiếc USB adapter. Ngoài khả năng kích hoạt tính năng Dolby Audio 7.1 và SoundID mà đây còn là cầu nối giúp cho chiếc tai nghe kết nối với những thiết bị, bao gồm PC Gaming, laptop, máy chơi game console và các thiết bị mobile thông qua cổng 3.5mm. Xứng đáng là một chiếc tai nghe có dây đa dụng cho mọi nhu cầu của một game thủ.',
        1, 'https://product.hstatic.net/200000722513/product/thumbtainghe_dcded1dce11c468fa3b139e9a82b8efd_0b9f408fdc0445d1b1269484afc45aad.png'
    );
