INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe Corsair HS65 Surround Carbon', 'tai-nghe-corsair-hs65-surround-carbon', 5,
        '{"Thương hiệu:": "Corsair", "Bảo hành:": "24 Tháng", "Model:": "Tai nghe Corsair HS65 Surround Carbon", "Màu sắc:": "Đen", "Kiểu tai nghe": "Over-ear", "Kiểu kết nối:": "Có dây", "Microphone:": "Có (không thể tháo rời)", "Âm thanh:": "Dolby Audio", "Âm thanh vòm:": "Có", "Trọng lượng:": "282g", "Tần số :": "20Hz - 20 kHz", "Trở kháng:": "32 Ohms @ 1 kHz", "Độ nhạy:": "114dB (+/-3dB)", "Drivers:": "50mm", "Phần mềm:": "Audio CUE", "Chiều dài cáp:": "1.8m", "Tương thích:": "PC, Mac, PlayStation 4/5, Xbox One / Series XS, Nintendo Switch, Thiết bị di động", "Khả năng cách âm:": "Có"}',
        'Đắm chìm vào thế giới game với Tai nghe Corsair HS65 Surround Carbon – sự kết hợp hoàn hảo giữa âm thanh sống động, sự thoải mái vượt trội và phong cách tinh tế. Được thiết kế dành cho game thủ yêu cầu chất lượng cao, HS65 Surround mang đến trải nghiệm âm thanh vòm 7.1 chân thực trên PC thông qua phần mềm iCUE, giúp bạn nghe rõ từng bước chân đối thủ hay tiếng súng vang dội từ mọi hướng. Với trình điều khiển neodymium 50mm tùy chỉnh, tai nghe này tái tạo dải âm rộng, rõ ràng từ âm trầm sâu lắng đến âm cao sắc nét.\n\nSự thoải mái là yếu tố cốt lõi: với trọng lượng siêu nhẹ chỉ 282g và đệm tai bằng mút hoạt tính bọc da tổng hợp mềm mại, HS65 Surround đảm bảo bạn có thể chơi game hàng giờ mà không cảm thấy khó chịu. Khung nhôm chắc chắn nhưng linh hoạt đảm bảo độ bền bỉ và vừa vặn hoàn hảo với mọi kích cỡ đầu.\n\nGiao tiếp rõ ràng là chìa khóa chiến thắng. Micro đa hướng được chứng nhận Discord với chức năng lật để tắt tiếng (flip-to-mute) thu giọng nói của bạn một cách rõ ràng và loại bỏ tiếng ồn xung quanh, đảm bảo đồng đội luôn nghe thấy bạn một cách hoàn hảo.\n\nKhả năng tương thích đa nền tảng linh hoạt qua jack cắm 3.5mm cho phép bạn sử dụng tai nghe trên PC, Mac, PlayStation, Xbox, Nintendo Switch và thiết bị di động. Riêng trên PC, USB adapter đi kèm còn mở khóa công nghệ Dolby Audio 7.1 Surround Sound và tính năng SoundID được hỗ trợ bởi Sonarworks, giúp bạn cá nhân hóa trải nghiệm âm thanh của riêng mình.\n\nVới thiết kế Carbon màu đen lịch lãm, Tai nghe Corsair HS65 Surround Carbon không chỉ là một công cụ chơi game mạnh mẽ mà còn là một phụ kiện thời trang đẳng cấp. Hãy sẵn sàng thống trị mọi trận chiến với lợi thế âm thanh tuyệt vời mà HS65 Surround mang lại!',
        1, 'https://product.hstatic.net/200000722513/product/thumbtainghe_09cc127393684af58143cd53500bb166_0f72c877ac704fc79ad84a255e54128e.png'
    );
