INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe Corsair HS55 Surround Carbon', 'tai-nghe-corsair-hs55-surround-carbon', 5,
        '{"Thương hiệu": "Corsair", "Bảo hành": "24 Tháng", "Model": "Tai nghe Corsair HS55 Surround Carbon", "Màu sắc": "Đen", "Kiểu tai nghe": "Over-ear", "Kiểu kết nối": "Có dây", "Microphone": "Có (không thể tháo rời)", "Âm thanh": "Dolby Audio", "Âm thanh vòm": "Có", "Trọng lượng": "284g", "Tần số": "20Hz - 20 kHz", "Trở kháng": "32k Ohms @ 1 kHz", "Độ nhạy": "114dB (+/-3dB)", "Drivers": "50mm", "Phần mềm": "Audio CUE", "Chiều dài cáp": "1.8m", "Tương thích": "PC, Mac, PlayStation 4/5, Xbox One / Series XS, Nintendo Switch, Mobile devices", "Khả năng cách âm": "Có"}',
        'Đắm chìm vào thế giới game với Tai nghe Corsair HS55 Surround Carbon – sự kết hợp hoàn hảo giữa âm thanh sống động, sự thoải mái vượt trội và thiết kế bền bỉ. Với âm thanh vòm 7.1 ảo được kích hoạt qua bộ chuyển đổi USB đi kèm, bạn sẽ nghe rõ từng bước chân của kẻ thù, từng tiếng súng nổ và chìm đắm hoàn toàn vào không gian game chân thực, mang lại lợi thế chiến thuật rõ rệt.\n\nĐược thiết kế cho các phiên chơi game kéo dài, HS55 Surround Carbon sở hữu trọng lượng siêu nhẹ chỉ 273g và đệm tai nghe bằng mút hoạt tính bọc da cao cấp, mang đến cảm giác êm ái, thoáng khí, không gây áp lực dù sử dụng trong nhiều giờ liên tục. Microphone đa hướng tích hợp đảm bảo giọng nói của bạn luôn được truyền tải rõ ràng và sắc nét đến đồng đội, loại bỏ tiếng ồn xung quanh, cùng tính năng gập mic để tắt tiếng (flip-to-mute) tiện lợi.\n\nKhung kim loại chắc chắn cùng lớp hoàn thiện màu Carbon tinh tế không chỉ tạo nên vẻ ngoài mạnh mẽ mà còn đảm bảo độ bền vượt thời gian. Điều khiển âm lượng và tắt tiếng dễ dàng ngay trên chụp tai.\n\nTương thích đa nền tảng từ PC, Mac (với âm thanh vòm 7.1 qua USB), cho đến PlayStation, Xbox, Nintendo Switch và thiết bị di động (qua jack 3.5mm), Corsair HS55 Surround Carbon chính là lựa chọn lý tưởng để nâng tầm trải nghiệm âm thanh và giao tiếp của bạn.',
        1, 'https://product.hstatic.net/200000722513/product/thumbtainghe_c5847b1d157747aa8f173645a679827f_b2ff9f1558cc43e3a6b6ce6a0b9fcc8e.png'
    );
