INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe Corsair Virtuoso RGB Wireless - Carbon (CA-9011185-AP)', 'corsair-virtuoso-rgb-wireless-carbon', 5,
        '{"Kiểu tai nghe:": "Over-ear", "Kiểu kết nối:": "Không dây, USB", "LED:": "RGB có thể chỉnh qua ICUE", "Chuẩn kết nối:": "USB/ Không dây/ Jack 3.5mm", "Microphone:": "Có thể tháo rời", "Trở kháng:": "2.0k Ohm", "Tần số:": "100Hz - 10kHz", "Khả năng cách âm:": "Có", "Chất liệu khung:": "Kim loại", "Chất liệu đệm tai nghe:": "Da", "Phụ kiện đi kèm:": "Tai nghe, dây kết nối, bộ thu sóng, Tài liệu hướng dẫn sử dụng", "Tương thích:": "PC / PS4"}',
        'Tai nghe Corsair Virtuoso RGB Wireless - Carbon (CA-9011185-AP) là dòng tai nghe gaming cao cấp, mang đến trải nghiệm âm thanh đỉnh cao, kết nối không dây liền mạch và thiết kế sang trọng. Với trình điều khiển âm thanh neodymium siêu mịn 50mm, Virtuoso RGB Wireless tái tạo mọi âm thanh với độ chi tiết đáng kinh ngạc, từ tiếng bước chân nhỏ nhất đến những vụ nổ hoành tráng, giúp bạn hoàn toàn đắm chìm trong thế giới game. Công nghệ âm thanh vòm Dolby® 7.1 mang đến âm thanh định vị chính xác, cho bạn lợi thế vượt trội trong mọi cuộc giao tranh. Kết nối không dây SLIPSTREAM CORSAIR siêu tốc với độ trễ cực thấp đảm bảo tín hiệu ổn định, không bị gián đoạn, cho phép bạn tự do di chuyển mà không ảnh hưởng đến chất lượng âm thanh. Ngoài ra, bạn còn có thể kết nối qua cổng USB hoặc jack 3.5mm để linh hoạt sử dụng với nhiều thiết bị khác nhau. Micro thu âm chất lượng phòng thu có thể tháo rời, trang bị đèn LED báo hiệu trạng thái, mang đến giọng nói rõ ràng, sắc nét cho đồng đội. Đệm tai bằng mút hoạt tính bọc da cao cấp và khung tai nghe bằng nhôm bền bỉ, trọng lượng nhẹ, kết hợp với thiết kế công thái học, mang lại sự thoải mái tối đa ngay cả khi sử dụng trong thời gian dài. Hệ thống đèn RGB tùy chỉnh với hàng triệu màu sắc và hiệu ứng đa dạng trên cả hai củ tai, cho phép bạn thể hiện cá tính và đồng bộ với hệ sinh thái Corsair iCUE. Pin sạc cho thời lượng sử dụng lên đến 20 giờ, đáp ứng nhu cầu chơi game, làm việc và giải trí liên tục. Tai nghe Corsair Virtuoso RGB Wireless - Carbon (CA-9011185-AP) là sự lựa chọn hoàn hảo cho các game thủ chuyên nghiệp và những người đam mê âm thanh muốn sở hữu một thiết bị đa năng, hiệu suất cao và phong cách.',
        1, 'https://product.hstatic.net/200000722513/product/9011185-ap-gallery-virtuoso-carbon-01_e6d0975e928c4189883b4cc65b374786_e5fc1605bae6453a965f5b4a9c756336.png'
    );
