INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe HP HyperX Cloud III Black', 'tai-nghe-hp-hyperx-cloud-iii-black', 27,
        '{"Hãng sản xuất": "HP HyperX", "Model": "Cloud III BLACK", "Bảo hành": "24 Tháng", "Tần số đáp ứng": "10Hz-21kHz", "Chiều dài dây cáp": "Cáp tai nghe 1.2m, Cáp USB dongle 1m3", "Màng loa": "53mm", "Loại khung": "Nhôm", "Độ nhạy": "-42dBV (0dB = 1V / Pa ở 1kHz)", "Kết nối thông qua jack": "3.5mm, USB", "Phụ kiện đi kèm": "Microphone, soundcard USB", "Tương thích": "- PC, PS5, PS4, Xbox Series X|S, Xbox One, Nintendo Switch, Mac và các thiết bị di động. Các đầu nối 3,5mm, USB-A và USB-C", "Âm thanh vòm": "7.1"}',
        'Tai nghe HP HyperX Cloud III Black mang đến trải nghiệm âm thanh đỉnh cao cho game thủ và người yêu nhạc. Với thiết kế hiện đại, màu đen huyền bí, chiếc tai nghe này không chỉ là một phụ kiện chơi game mà còn là biểu tượng phong cách. HyperX Cloud III được trang bị củ loa Neodymium kích thước lớn 53mm, mang lại âm thanh sống động, chi tiết và mạnh mẽ, giúp bạn dễ dàng nhận diện mọi âm thanh trong game, từ tiếng bước chân kẻ địch đến những vụ nổ hoành tráng. Màng loa được tinh chỉnh đặc biệt để tối ưu hóa âm thanh vòm, tạo ra không gian âm thanh 3D chân thực, nâng cao khả năng định vị và phản xạ nhanh nhạy trong mọi tình huống. Đệm tai bằng mút hoạt tính cao cấp bọc da PU mềm mại, ôm sát tai, mang lại sự thoải mái tối đa ngay cả khi sử dụng trong thời gian dài. Khung tai nghe làm từ nhôm cao cấp, vừa chắc chắn, bền bỉ, vừa nhẹ nhàng, giảm áp lực lên đầu. Micro thu âm có thể tháo rời, tích hợp công nghệ khử tiếng ồn, đảm bảo giọng nói của bạn luôn rõ ràng, trong trẻo khi giao tiếp với đồng đội hoặc tham gia các cuộc gọi. Cổng kết nối USB-C đa dụng, tương thích với nhiều thiết bị như PC, PlayStation 5, Xbox Series X|S, Nintendo Switch và các thiết bị di động khác. Dây cáp dài, bọc vải chống rối, tăng cường độ bền và sự tiện lợi khi sử dụng. Tai nghe HP HyperX Cloud III Black là sự kết hợp hoàn hảo giữa hiệu năng âm thanh vượt trội, thiết kế công thái học và độ bền bỉ, là lựa chọn lý tưởng cho mọi game thủ chuyên nghiệp.',
        1, 'https://product.hstatic.net/200000722513/product/hinh-1_06b4ca8ba58943b19ace76203ecc8b01_db698b18b73a43a7955f45f105ba989e.gif'
    );
