INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe Logitech G733 LIGHTSPEED Wireless White', 'tai-nghe-logitech-g733-lightspeed-wireless-white', 13,
        '{"Thương hiệu:": "Logitech", "Bảo hành:": "24 tháng", "Series/Model:": "G733 LightSpeed Wireless White", "Màu sắc:": "Trắng", "Kiểu tai nghe:": "Over-ear", "Kiểu kết nối:": "Không dây", "LED:": "Có LED RGB", "Chuẩn kết nối:": "Receiver USB type A", "Microphone:": "Có thể tháo rời", "Trở kháng:": "1kHz 32 Ohm", "Tần số:": "20Hz - 20KHz", "Khả năng cách âm:": "Có", "Chất liệu khung:": "Nhựa", "Chất liệu đệm tai nghe:": "Vải thoáng khí", "Phụ kiện đi kèm:": "Hướng dẫn sử dụng, Receiver USB, Cáp sạc USB type A to USB type C", "Tương thích:": "Windows 7 trở lên, MacOS X 10.12 trở lên, PlayStation 4", "Chức năng đặc biệt:": "Công nghệ kết nối không dây Lightspeed độc quyền Logitech"}',
        'Tai nghe Logitech G733 LIGHTSPEED Wireless White là một lựa chọn tuyệt vời cho các game thủ và người dùng yêu cầu chất lượng âm thanh cao cùng sự tiện lợi không dây. Với công nghệ không dây LIGHTSPEED tiên tiến, tai nghe mang đến kết nối siêu ổn định và độ trễ cực thấp, giúp bạn phản ứng nhanh nhạy trong mọi tình huống game. Thiết kế màu trắng tinh khôi, hiện đại và trọng lượng siêu nhẹ chỉ khoảng 278g, G733 tạo cảm giác thoải mái khi đeo trong thời gian dài. Đệm tai bằng bọt hoạt tính thoáng khí và dây đeo đầu mềm mại có thể điều chỉnh giúp phân bổ trọng lượng đều, giảm áp lực lên đầu. Màng loa PRO-G độc quyền của Logitech cho âm thanh sống động, chi tiết với dải tần rộng, tái hiện chân thực mọi âm thanh trong game, từ tiếng bước chân nhỏ nhất đến những vụ nổ hoành tráng. Công nghệ DTS Headphone:X v2.0 mang đến âm thanh vòm 7.1 thế hệ mới, giúp định vị chính xác nguồn âm thanh, mang lại lợi thế cạnh tranh vượt trội. Micrô kép với công nghệ lọc tiếng ồn Blue VO!CE tiên tiến giúp giọng nói của bạn trong trẻo, rõ ràng, loại bỏ tạp âm môi trường, đảm bảo giao tiếp hiệu quả với đồng đội. Pin sạc cho thời lượng sử dụng lên đến 29 giờ với đèn RGB tắt và khoảng 20 giờ khi bật đèn, thoải mái sử dụng cả ngày dài. Hệ thống đèn LED RGB LIGHTSYNC tùy chỉnh với hơn 16.8 triệu màu, cho phép bạn thể hiện phong cách cá nhân và đồng bộ với các thiết bị Logitech G khác. Tai nghe còn hỗ trợ kết nối đa nền tảng qua cổng USB-C, tương thích với PC, PlayStation, Nintendo Switch và các thiết bị di động. Bao gồm cáp sạc USB-C và bộ thu USB LIGHTSPEED.',
        1, 'https://product.hstatic.net/200000722513/product/-g733-lightspeed-wireless-white-66666_142abc4187744aa7b7fe009d7486696c_5a3256dbfe0746d9a5549eb5e11bf773.png'
    );
