INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe Gaming Rapoo VH160', 'tai-nghe-gaming-rapoo-vh160', 29,
        '{"Thương hiệu": "Rapoo", "Bảo hành": "24 tháng", "Series/Model": "VH160", "Màu sắc": "Đen", "Kiểu tai nghe": "Over-ear", "Độ nhạy": "103 dB", "LED": "RGB", "Kết nối": "Có dây", "Microphone": "Có", "Trở kháng": "32 Ohm", "Tần số": "20 Hz - 20 kHz", "Khả năng cách âm": "Có", "Trọng lượng": "340 g"}',
        'Tai nghe Gaming Rapoo VH160 là lựa chọn hoàn hảo cho các game thủ yêu cầu chất lượng âm thanh đỉnh cao và trải nghiệm chơi game sống động. Với thiết kế hầm hố, hiện đại, VH160 không chỉ là một phụ kiện gaming mà còn là tuyên ngôn về phong cách của bạn.\n\nHệ thống âm thanh vòm 7.1 mang đến khả năng định vị âm thanh cực kỳ chính xác, giúp bạn dễ dàng xác định vị trí kẻ địch, tận hưởng trọn vẹn từng tiếng bước chân, tiếng súng hay hiệu ứng môi trường trong game. Màng loa kích thước lớn 50mm cung cấp dải âm bass mạnh mẽ, âm treble trong trẻo, tái tạo âm thanh chi tiết và đầy đặn, làm cho mọi cuộc chiến trở nên chân thực hơn bao giờ hết.\n\nMicrophone đa hướng có khả năng khử tiếng ồn, đảm bảo giọng nói của bạn luôn rõ ràng, rành mạch khi giao tiếp với đồng đội, ngay cả trong môi trường ồn ào. Đệm tai nghe được làm từ chất liệu cao cấp, mềm mại và thoáng khí, ôm trọn vành tai, mang lại sự thoải mái tối đa ngay cả trong những phiên chơi game kéo dài.\n\nKhung tai nghe chắc chắn, có thể điều chỉnh linh hoạt phù hợp với mọi kích cỡ đầu. Đèn LED RGB với hiệu ứng ánh sáng đa dạng không chỉ tăng thêm phần cuốn hút cho tai nghe mà còn tạo không khí sôi động cho góc gaming của bạn. Dây cáp bọc dù bền bỉ, chống rối hiệu quả, tăng cường tuổi thọ cho sản phẩm.\n\nRapoo VH160 tương thích rộng rãi với nhiều nền tảng như PC, laptop, PS4, Xbox One (có thể cần adapter). Đây là một sự đầu tư xứng đáng cho bất kỳ game thủ nào muốn nâng tầm trải nghiệm giải trí của mình.',
        1, 'https://product.hstatic.net/200000722513/product/gearvn-tai-nghe-gaming-rapoo-vh160-1_03c10c9a8cdd46039cc536c3292f663f_9288f6c2b45f4996892b94306f135188.png'
    );
