INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe Logitech G435 Lightspeed Wireless - White', 'tai-nghe-logitech-g435-lightspeed-wireless-white', 13,
        '{"Thương hiệu": "Logitech", "Model": "G435", "Trọng lượng": "165 g", "Kích thước": "163 x 170 x 71 mm", "Driver": "40 mm", "Kết nối": "LIGHTSPEED không dây qua USB 2.0 (Type-A), Bluetooth, tương thích Dolby Atmos, Tempest 3D AudioTech và Windows Sonic Spatial Sound", "Phạm vi kết nối": "10 m", "Pin": "Thời lượng 18 giờ, sạc qua USB-C, dây sạc USB-A sang USB-C", "Phụ kiện đi kèm": "Tai nghe G435, USB-A LIGHTSPEED, dây sạc USB-C sang USB-A, tài liệu hướng dẫn sử dụng", "Độ nhạy tần số": "20 Hz - 20 kHz", "Trở kháng": "45 Ω (bị động)", "Độ nhạy": "83,1 dB SPL/mW"}',
        'Tai nghe Logitech G435 Lightspeed Wireless phiên bản màu Trắng mang đến trải nghiệm âm thanh đỉnh cao, kết hợp với sự tự do không dây vượt trội. Được thiết kế đặc biệt cho game thủ và người yêu âm nhạc, G435 sở hữu công nghệ Lightspeed độc quyền của Logitech, đảm bảo kết nối không dây ổn định, độ trễ cực thấp, mang lại phản hồi tức thời trong mọi tình huống, từ những trận game kịch tính đến những bản nhạc yêu thích. Trọng lượng siêu nhẹ chỉ 255 gram giúp người dùng thoải mái sử dụng trong nhiều giờ liền mà không gây mỏi hay khó chịu. Màng loa Neodymium 40mm cho âm thanh rõ ràng, chi tiết với dải tần rộng, tái tạo âm bass mạnh mẽ và âm treble sắc nét, giúp bạn đắm chìm hoàn toàn vào thế giới ảo hoặc tận hưởng âm nhạc trọn vẹn. Micro kép tích hợp công nghệ beamforming giúp lọc tiếng ồn xung quanh, chỉ thu giọng nói của bạn, đảm bảo liên lạc rõ ràng với đồng đội hoặc trong các cuộc gọi. Pin sạc tích hợp cho thời lượng sử dụng lên đến 18 giờ, đáp ứng nhu cầu sử dụng cả ngày. Thiết kế hiện đại, tối giản với tông màu trắng tinh khôi, phù hợp với mọi không gian làm việc và giải trí. Tương thích đa nền tảng, bao gồm PC, PlayStation, Nintendo Switch và thiết bị di động thông qua cổng USB-C hoặc Bluetooth, mang đến sự linh hoạt tối đa.',
        1, 'https://product.hstatic.net/200000722513/product/tech-g435-lightspeed-wireless-white-1_9af011ebd64e4a7990c4747a3576a909_041ae18de1764e3b96bf687d649c0c6c.jpg'
    );
