INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe DareU EH722X 7.1 Pink', 'tai-nghe-dareu-eh722x-7-1-pink', 25,
        '{"Hãng sản xuất:": "DareU", "Model:": "EH722X Pink", "Kiểu tai nghe:": "Tai nghe chụp tai", "Loại tai nghe:": "Có dây", "Đèn LED:": "Multi", "Kết nối:": "USB", "Driver:": "Φ50mm", "Công nghệ tích hợp:": "Giả lập 7.1", "Dải tần số:": "20Hz-20KHz", "Chất liệu:": "Da mềm, gọng kim loại", "Độ dài dây:": "2.4m"}',
        'Tai nghe DareU EH722X 7.1 Pink là một lựa chọn tuyệt vời cho game thủ và những người yêu âm nhạc, mang đến trải nghiệm âm thanh vòm sống động và thiết kế thời trang. Với công nghệ âm thanh vòm 7.1 ảo, EH722X tái tạo âm thanh chính xác, giúp bạn dễ dàng định vị kẻ địch trong game, nghe rõ từng bước chân hay tiếng súng, mang lại lợi thế cạnh tranh. Chất lượng âm thanh của tai nghe được đánh giá cao, với âm bass mạnh mẽ, âm trung rõ ràng và âm cao chi tiết, phù hợp cho cả nhu cầu chơi game lẫn thưởng thức âm nhạc đa thể loại. Thiết kế của EH722X gây ấn tượng với tông màu hồng pastel nữ tính, cùng với các chi tiết tinh tế, tạo nên vẻ ngoài độc đáo và thu hút. Đệm tai nghe được làm từ chất liệu da mềm mại, thoáng khí, ôm trọn tai, mang lại cảm giác thoải mái ngay cả khi sử dụng trong thời gian dài. Khung tai nghe có thể điều chỉnh linh hoạt, phù hợp với nhiều kích cỡ đầu khác nhau. Microphone của DareU EH722X có khả năng khử tiếng ồn, thu âm giọng nói rõ ràng, giúp bạn giao tiếp hiệu quả với đồng đội trong các trận đấu game. Đèn LED RGB trên củ tai và micro tạo hiệu ứng ánh sáng bắt mắt, tăng thêm phần hưng phấn khi chơi game, đặc biệt trong môi trường thiếu sáng. Kết nối USB tiện lợi, dễ dàng cắm và sử dụng trên nhiều thiết bị. DareU EH722X 7.1 Pink không chỉ là một chiếc tai nghe chơi game, mà còn là một phụ kiện thời trang, thể hiện cá tính của người dùng.',
        1, 'https://product.hstatic.net/200000722513/product/vn-tai-nghe-dareu-eh722x-7-1-pink-666_47afec73c97a48a2a3c16fa53ed09245_17053b8a0abe461d93f83107ffabc176.jpg'
    );
