INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe DareU EH722X 7.1 White', 'tai-nghe-dareu-eh722x-7-1-white', 25,
        '{"Hãng sản xuất": "DareU", "Model": "EH722X 7.1 White", "Kiểu tai nghe": "Tai nghe chụp tai", "Loại tai nghe": "Có dây", "Đèn LED": "Multi", "Kết nối": "USB", "Driver": "Φ50mm", "Công nghệ tích hợp": "Giả lập 7.1", "Dải tần số": "20Hz-20KHz", "Chất liệu": "Da mềm, gọng kim loại", "Độ dài dây": "2.4m"}',
        'Tai nghe DareU EH722X 7.1 White mang đến trải nghiệm âm thanh vòm sống động, chân thực, đưa bạn vào thế giới game hoặc âm nhạc một cách trọn vẹn. Với công nghệ âm thanh 7.1 ảo, mọi âm thanh, từ tiếng bước chân kẻ địch, tiếng súng, đến từng giai điệu âm nhạc, đều được tái hiện chi tiết, rõ ràng, giúp bạn có lợi thế trong mọi cuộc chơi và thưởng thức âm nhạc đỉnh cao. Thiết kế của EH722X gây ấn tượng với tông màu trắng tinh khôi, hiện đại, kết hợp với hệ thống đèn LED RGB Aura lung linh, tạo nên điểm nhấn phong cách cho góc máy tính của bạn. Earcup bọc da cao cấp, đệm tai dày dặn, mềm mại mang lại cảm giác thoải mái tối đa ngay cả khi sử dụng trong thời gian dài. Khung tai nghe được làm từ chất liệu cao cấp, bền bỉ, có khả năng điều chỉnh linh hoạt để phù hợp với mọi kích cỡ đầu. Micro đa hướng có khả năng lọc tiếng ồn hiệu quả, đảm bảo giọng nói của bạn luôn rõ ràng, trong trẻo khi giao tiếp với đồng đội. Tai nghe DareU EH722X 7.1 White còn được trang bị driver âm thanh kích thước lớn, cho âm bass mạnh mẽ, âm treble sắc nét, tái tạo dải tần âm thanh rộng. Dây cáp bọc dù chắc chắn, chống rối, tăng cường độ bền. Sản phẩm này là sự lựa chọn hoàn hảo cho các game thủ chuyên nghiệp và những người yêu âm nhạc, mong muốn sở hữu một thiết bị âm thanh đa năng, chất lượng cao với vẻ ngoài thời thượng.',
