INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe Logitech G435 Lightspeed Wireless - Blue Raspberry', 'tai-nghe-logitech-g435-lightspeed-wireless-blue-raspberry', 13,
        '{"Thương hiệu": "Logitech", "Model": "G435", "Trọng lượng": "165 g", "Kích thước": "163 x 170 x 71 mm", "Driver": "40 mm", "Kết nối": "LIGHTSPEED không dây qua USB 2.0 (Type-A), Bluetooth, tương thích Dolby Atmos, Tempest 3D AudioTech và Windows Sonic Spatial Sound", "Phạm vi kết nối": "10 m", "Pin": "Thời lượng 18 giờ, sạc qua USB-C, dây sạc USB-A sang USB-C", "Phụ kiện đi kèm": "Tai nghe G435, USB-A LIGHTSPEED, dây sạc USB-C sang USB-A, tài liệu hướng dẫn sử dụng", "Độ nhạy tần số": "20 Hz - 20 kHz", "Trở kháng": "45 Ω (bị động)", "Độ nhạy": "83,1 dB SPL/mW"}',
        'Tai nghe Logitech G435 Lightspeed Wireless - Blue Raspberry mang đến trải nghiệm âm thanh đỉnh cao, thiết kế siêu nhẹ và kết nối không dây tốc độ cao, hoàn hảo cho game thủ và người dùng yêu thích âm nhạc. Với trọng lượng chỉ 165 gram, G435 là một trong những tai nghe gaming không dây nhẹ nhất, giảm thiểu áp lực lên đầu và tai, cho phép bạn thoải mái sử dụng trong thời gian dài. Màu Xanh Mâm Xôi (Blue Raspberry) thời trang, năng động, mang đến phong cách riêng biệt, thể hiện cá tính của bạn. Công nghệ không dây Lightspeed độc quyền của Logitech đảm bảo kết nối siêu ổn định và độ trễ cực thấp, mang lại lợi thế cạnh tranh trong mọi trận đấu game. Ngoài ra, G435 còn hỗ trợ kết nối Bluetooth, cho phép bạn dễ dàng chuyển đổi giữa chơi game trên PC, console hoặc nghe nhạc trên điện thoại, tablet. Micro định hướng tích hợp giúp thu âm giọng nói rõ ràng, loại bỏ tiếng ồn xung quanh, đảm bảo mọi giao tiếp của bạn đều mượt mà. Màng loa 40mm mang đến âm thanh sống động, chi tiết, tái tạo âm bass mạnh mẽ và âm treble sắc nét, giúp bạn đắm chìm hoàn toàn vào thế giới game hoặc thưởng thức âm nhạc trọn vẹn. Pin sạc cho thời lượng sử dụng ấn tượng lên đến 18 giờ chỉ với một lần sạc. Vỏ ngoài được làm từ vật liệu tái chế, thể hiện cam kết của Logitech về môi trường. Nút điều khiển trực quan trên củ tai giúp bạn dễ dàng điều chỉnh âm lượng, bật/tắt micro và quản lý kết nối. Logitech G435 Lightspeed Wireless - Blue Raspberry là sự kết hợp hoàn hảo giữa hiệu năng, sự thoải mái và phong cách, là lựa chọn lý tưởng cho mọi nhu cầu giải trí đa phương tiện của bạn.',
        1, 'https://product.hstatic.net/200000722513/product/-lightspeed-wireless-blue-raspberry-2_e3ec13886fe44b5683dcc53aca9830d4_2166ec5e683b4208bd1425bacd56bede.jpg'
    );
