INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe Rapoo VM150 In-ear', 'tai-nghe-rapoo-vm150-in-ear', 29,
        '{"Thương hiệu:": "Rapoo", "Bảo hành:": "24 tháng", "Model:": "Rapoo VM150 In-ear", "Màu sắc:": "Đen - Đỏ", "Kiểu kết nối:": "Có dây", "Kiểu tai nghe:": "In-ear", "Đèn LED:": "Không", "Chuẩn kết nối:": "3.5mm", "Microphone:": "1 mic in-line control và 1 mic rời nhằm tăng khả năng đàm thoại", "Trở kháng:": "Tai nghe: 16 ohm; Micro: 2,2k ohm", "Tần số :": "20-20000Hz", "Độ nhạy:": "Tai nghe: 92 ±3dB; Micro: -38 ±3dB", "Khả năng cách âm:": "Có", "Chất liệu đệm tai nghe :": "Nút silicon cao cấp", "Tương thích:": "PC, laptop, điện thoại"}',
        'Rapoo VM150 In-ear là một trong những dòng tai nghe máy tính chơi game, sử dụng tốt cho cả thiết bị di động. Sở hữu thiết kế góc cạnh mang đậm chất phong cách gaming hiện đại. Thiết kế mic rời đi cùng mic in-line nhằm nâng cao khả năng trò chuyện, giao tiếp trong game.\n\n**Phụ kiện đầy đủ**\nMột trong những dòng tai nghe thuộc series Rapoo VH, được hãng trang bị kèm 1 mic rời, 1 dây chia jack 3.5, 2 cặp tip thay thế với size khác nhau, phù hợp với kích cỡ lỗ tai khác nhau. Được thiết kế dưới dạng nhét tai nên người dùng có thể tận hưởng âm thanh chất lượng, sống động, hạn chế những tạp âm bên ngoài sẽ ảnh hưởng.\n\n**Tương thích nhiều nền tảng**\nVới kết nối chuẩn 3.5mm, tai nghe Rapoo VM150 In-ear có thể sử dụng với nhiều thiết bị khác nhau, đặc biệt là các thiết bị chơi game di động. Dòng tai nghe gaming dưới 1 triệu đáng sở hữu nhất hiện nay.',
        1, 'https://product.hstatic.net/200000722513/product/vm150_2020_1-5_compressed_e0666b8c5c904e58a4063351c973386d_5bb88993ed354fc994bc918fa21389ef.jpg'
    );
