INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe Razer BlackShark V2 X', 'tai-nghe-razer-blackshark-v2-x', 15,
        '{"Thương hiệu": "Razer", "Bảo hành": "24 tháng", "Model": "Razer BlackShark V2 X", "Màu": "Đen", "Kiểu tai nghe": "Over-ear", "Tần số phản hồi": "Tai nghe: 12 Hz - 28.000 Hz, Micro: 100 Hz - 10.000 Hz", "Trở kháng": "32 Ohm (1 kHz)", "Độ nhạy": "Tai nghe: 100 dBSPL/mW (1 kHz), Micro: -42 dB V/Pa (1 kHz)", "Kích thước trình điều khiển (Drivers)": "50 mm", "Loại trình điều khiển": "Razer™ Triforce", "Chất liệu đệm tai nghe": "Đệm xốp thoáng khí", "Chất liệu khung": "Kim loại cao cấp", "Khử tiếng ồn": "Khử tiếng ồn thụ động nâng cao", "Kiểu kết nối": "Có dây (Analog 3,5 mm)", "Chiều dài cáp": "1,3 m", "Cân nặng": "240 g", "Kiểu micro": "Razer™ Hyperclear Cardioid mic", "Mã hóa vòm ảo": "Âm thanh vòm 7.1", "Khả năng tương thích": "PC, Mac, PS4, Xbox One, Nintendo Switch và thiết bị di động (có cổng 3,5 mm khả dụng)"}',
        'Tai nghe Razer BlackShark V2 X mang đến trải nghiệm âm thanh đỉnh cao, lý tưởng cho game thủ và người yêu âm nhạc. Với thiết kế trùm tai (Over-Ear) cực kỳ thoải mái, phần đệm tai làm từ mút hoạt tính thoáng khí và bọc da PU mềm mại, bạn có thể đeo tai nghe trong nhiều giờ liền mà không cảm thấy khó chịu. Khung tai nghe được gia cố bằng nhựa ABS bền bỉ, đảm bảo độ chắc chắn và tuổi thọ cao.\n\nĐiểm nhấn của BlackShark V2 X nằm ở chất lượng âm thanh. Driver Razer TriForce Titanium 50mm mang đến âm thanh rõ ràng, chi tiết với dải tần rộng, giúp bạn nghe rõ từng bước chân địch, tiếng vũ khí hay những bản nhạc yêu thích. Công nghệ HyperClear Cardioid Mic với khả năng thu âm định hướng giúp giọng nói của bạn luôn trong trẻo, loại bỏ tiếng ồn xung quanh, tối ưu cho giao tiếp trong game hay các cuộc gọi.\n\nTai nghe còn hỗ trợ âm thanh vòm 7.1 tiên tiến (chỉ có trên Windows 10 64-bit), tạo ra không gian âm thanh 3D sống động, giúp bạn định vị kẻ địch dễ dàng hơn trong mọi trận chiến. Kết nối qua jack 3.5mm phổ biến, tương thích với đa dạng thiết bị như PC, PlayStation, Xbox, Nintendo Switch, Mac và điện thoại di động. Nút điều khiển âm lượng và tắt tiếng mic tích hợp trên dây cáp tiện lợi cho phép bạn tùy chỉnh nhanh chóng. Tai nghe Razer BlackShark V2 X là sự lựa chọn hoàn hảo cho những ai tìm kiếm hiệu suất âm thanh xuất sắc, sự thoải mái vượt trội và độ bền bỉ trong một thiết kế gaming chuyên nghiệp.',
        1, 'https://product.hstatic.net/200000722513/product/vn-tai-nghe-razer-blackshark-v2-photoroom_e7d768a99de7423b9d24382728d710ce.png'
    );
