INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe HyperX Cloud Stinger Core II', 'tai-nghe-hyperx-cloud-stinger-core-ii', 27,
        '{"Drivers": "40mm", "Màu sắc": "Đen", "Kiểu tai nghe": "Over-ear", "Tần số": "20Hz - 20.000Hz", "Kết nối": "Có dây", "Micro": "Có thể tháo rời", "Trở kháng": "16 Ω", "Chiều dài cáp": "1,3m", "Trọng lượng": "215g"}',
        'Tai nghe HyperX Cloud Stinger Core II là lựa chọn hoàn hảo cho game thủ muốn trải nghiệm âm thanh đỉnh cao với mức giá phải chăng. Với thiết kế công thái học, đệm tai mềm mại và headband có thể điều chỉnh, Stinger Core II mang lại sự thoải mái tối đa ngay cả trong những phiên chơi game kéo dài. Màng loa 50mm được tinh chỉnh cẩn thận cho âm thanh vòm sống động, giúp bạn nghe rõ từng bước chân địch thủ, tiếng súng hay hiệu ứng môi trường, mang đến lợi thế cạnh tranh vượt trội. Microphone tích hợp có khả năng khử tiếng ồn, đảm bảo giọng nói của bạn luôn rõ ràng và mạch lạc khi giao tiếp với đồng đội. Đầu nối 3.5mm tương thích với nhiều nền tảng, bao gồm PC, PlayStation, Xbox, Nintendo Switch và các thiết bị di động. Khung thép bền bỉ đảm bảo tuổi thọ sản phẩm, trong khi nút điều chỉnh âm lượng trên củ tai giúp bạn dễ dàng thao tác. HyperX Cloud Stinger Core II không chỉ là một chiếc tai nghe gaming, mà còn là người bạn đồng hành đáng tin cậy trên mọi đấu trường ảo.',
        1, 'https://product.hstatic.net/200000722513/product/thumbtainghe_499f42bf16fe47d28ab00bffb7bd5748_47730811ddaf40a0a969f4e4d49c7b27.png'
    );
