INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe Logitech G Pro X Gaming Black', 'tai-nghe-logitech-g-pro-x', 13,
        '{"Thương hiệu:": "Logitech", "Bảo hành:": "24 tháng", "Series/Model:": "Logitech G Pro X", "Màu sắc:": "Đen", "Kiểu tai nghe:": "Over-ear", "Kiểu kết nối:": "Có dây (dây cao su)", "LED:": "Không", "Chuẩn kết nối:": "3.5 mm / USB Type-A", "Microphone:": "Có thể tháo rời", "Trở kháng:": "35 Ohm", "Tần số:": "20Hz - 20KHz", "Khả năng cách âm:": "Có", "Chất liệu khung:": "Hợp kim", "Chất liệu đệm tai nghe:": "Da mềm", "Phụ kiện đi kèm:": "Hướng dẫn sử dụng, Cặp cúp đệm tai vải thoáng, USB sound card, Dây chia tín hiệu 1-to-2 3.5mm, Mic rời, Túi đựng", "Tương thích:": "Windows / MacOS", "Chức năng đặc biệt:": "Trang bị sound card rời"}',
        'Tai nghe Logitech G Pro X Gaming Black là một thiết bị âm thanh cao cấp, được thiết kế đặc biệt cho game thủ chuyên nghiệp, mang đến trải nghiệm âm thanh đỉnh cao và sự thoải mái tối ưu trong những trận chiến game kéo dài. Với màng loa Blue VO!CE tiên tiến, chiếc tai nghe này tái tạo âm thanh với độ chi tiết tuyệt vời, từ những bước chân nhỏ nhất đến tiếng súng vang dội, giúp bạn phản ứng nhanh nhạy và chiếm lợi thế trong mọi tình huống. Công nghệ microphone condenser rời chất lượng phòng thu, cùng bộ lọc Blue VO!CE, giúp giọng nói của bạn trở nên rõ ràng, chuyên nghiệp và loại bỏ tiếng ồn xung quanh hiệu quả, đảm bảo đồng đội luôn nghe rõ mệnh lệnh của bạn. Thiết kế earcup bọc da và mút hoạt tính mang lại cảm giác êm ái, không gây áp lực lên tai, ngay cả khi sử dụng trong thời gian dài. Khung tai nghe bằng thép và nylon gia cố bằng sợi thủy tinh đảm bảo độ bền bỉ và chắc chắn, sẵn sàng đồng hành cùng bạn qua mọi thử thách. Ngoài ra, Logitech G Pro X còn có khả năng tùy chỉnh âm thanh mạnh mẽ thông qua phần mềm G HUB, cho phép bạn điều chỉnh EQ, hiệu ứng âm thanh và cài đặt microphone theo sở thích cá nhân, tạo nên không gian âm thanh độc đáo của riêng mình. Chuẩn kết nối 3.5mm đa năng tương thích với nhiều thiết bị chơi game như PC, PlayStation, Xbox, Nintendo Switch và các thiết bị di động. Tai nghe Logitech G Pro X Gaming Black không chỉ là một phụ kiện, mà còn là một vũ khí bí mật giúp bạn nâng tầm trải nghiệm game và chinh phục mọi mục tiêu.',
        1, 'https://product.hstatic.net/200000722513/product/gvn_logitech_prox_79c556630c454086baf1bee06c577ab7_3471d9d886fd4dbe8ab5ae6bed9f4d78.png'
    );
