INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe HyperX Cloud Core Wireless', 'tai-nghe-hyperx-cloud-core-wireless', 27,
        '{"Kiểu tai nghe": "Over-ear", "Trọng lượng": "283 g", "Tần số": "10 Hz – 21 kHz", "Chụp tai": "Mút hoạt tính", "Pin": "Pin sạc", "Kết nối": "Wireless 2.4 GHz", "Tổng độ biến dạng sóng hài": "≤ 1%", "Trở kháng": "60 Ω", "Model": "HyperX Cloud Core Wireless", "Độ nhạy": "99 dBSPL/mW ở 1 kHz", "Thương hiệu": "Kingston", "Loại khung": "Nhôm"}',
        'Tai nghe HyperX Cloud Core Wireless mang đến trải nghiệm âm thanh không dây đỉnh cao, kết hợp sự thoải mái vượt trội và chất lượng âm thanh trung thực, giúp bạn đắm chìm hoàn toàn vào thế giới game và giải trí. Với công nghệ không dây 2.4GHz mạnh mẽ, tai nghe cung cấp kết nối ổn định và độ trễ thấp, đảm bảo mọi âm thanh đều được truyền tải một cách mượt mà và chính xác, giúp bạn phản ứng nhanh nhạy trong mọi tình huống. Đệm tai bằng mút hoạt tính (memory foam) cao cấp, bọc da mềm mại, cùng với phần đệm đầu có thể điều chỉnh, mang đến cảm giác êm ái, dễ chịu ngay cả khi sử dụng trong thời gian dài. Thiết kế gọng tai nghe chắc chắn, bền bỉ, phù hợp với mọi đối tượng game thủ. Driver Neodymium đường kính 53mm cho âm thanh sống động, rõ ràng với âm bass mạnh mẽ và âm treble sắc nét, giúp bạn nghe rõ từng bước chân, tiếng súng hay những giai điệu du dương. Microphone chống ồn có thể tháo rời, cho phép bạn giao tiếp rõ ràng với đồng đội hoặc loại bỏ khi không sử dụng. Thời lượng pin ấn tượng lên đến 80 giờ cho mỗi lần sạc đầy, bạn có thể thoải mái chơi game, xem phim hay nghe nhạc mà không lo gián đoạn. Khả năng tương thích rộng rãi với PC, PlayStation, Nintendo Switch và các thiết bị di động khác thông qua bộ chuyển đổi USB. HyperX Cloud Core Wireless không chỉ là một chiếc tai nghe, mà còn là người bạn đồng hành lý tưởng cho mọi nhu cầu giải trí của bạn.',
        1, 'https://product.hstatic.net/200000722513/product/hx-features-cloud-core-wireless_70150d6fc8b84627ad8f59a875a66d79_9b412bd5d9794286a8484bdefde44883.jpg'
    );
