INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe Gaming Logitech G335 Black', 'tai-nghe-gaming-logitech-g335-black', 13,
        '{"Thương hiệu:": "Logitech", "Model:": "Logitech G335", "Màu sắc:": "Đen", "Kiểu tai nghe:": "On-ear", "Kiểu kết nối:": "Có dây", "Đèn LED:": "Không", "Chuẩn kết nối:": "3.5", "Microphone:": "Có", "Dây:": "Dài 2m", "Tần số:": "1000 Hz", "Khả năng cách âm": "Có", "Chất liệu khung:": "Nhựa", "Chất liệu đệm tai nghe:": "Vải", "Tương thích:": "PC, Xbox, PlayStation, Nintendo Switch và các thiết bị di động có giắc âm thanh 3.5", "Tính năng:": "Kết nối đa thiết bị"}',
        'Tai nghe Gaming Logitech G335 Black mang đến trải nghiệm âm thanh sống động và đắm chìm cho mọi game thủ. Với thiết kế siêu nhẹ chỉ 240 gram, G335 mang lại sự thoải mái tối đa ngay cả trong những phiên chơi game kéo dài. Đệm tai bằng bọt hoạt tính mềm mại cùng dây đeo có thể điều chỉnh linh hoạt giúp ôm sát đầu, giảm thiểu áp lực và mang đến cảm giác dễ chịu. Màng loa neodymium 40mm được tinh chỉnh chuyên nghiệp cung cấp âm thanh rõ ràng, chi tiết với âm trầm mạnh mẽ, giúp bạn nghe rõ mọi bước chân địch, tiếng súng hay hiệu ứng trong game, từ đó nâng cao lợi thế cạnh tranh. Microphone có thể gập lên và tắt tiếng một cách dễ dàng, đảm bảo giọng nói của bạn luôn rõ ràng và sắc nét khi giao tiếp với đồng đội. Tích hợp bộ điều chỉnh âm lượng trên củ tai, cho phép bạn tùy chỉnh âm lượng nhanh chóng mà không cần rời khỏi trận chiến. Tương thích rộng rãi với PC, PlayStation, Xbox, Nintendo Switch và các thiết bị di động khác thông qua jack cắm 3.5mm, G335 là lựa chọn hoàn hảo cho mọi nền tảng chơi game. Kiểu dáng hiện đại, trẻ trung với tông màu đen cá tính, phù hợp với mọi dàn máy gaming. Chất liệu cao cấp đảm bảo độ bền bỉ theo thời gian. Logitech G335 Black - người bạn đồng hành lý tưởng cho mọi hành trình chinh phục thế giới ảo.',
        1, 'https://product.hstatic.net/200000722513/product/g335-6_e1253941f7e3484890523aa330eecdff_851c5cc9a279406081d5bc1cd9294522.jpg'
    );
