INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe HyperX Cloud Orbit S', 'tai-nghe-hyperx-cloud-orbit-s', 27,
        '{"Driver": "Bộ truyền động planar 100 mm", "Dải tần số": "10Hz–50.000Hz", "Loại kết nối": "USB Type A, USB Type C, 3.5mm", "Loại âm thanh": "Stereo + Âm thanh 3D + Headtracking", "Điều khiển âm thanh": "Trên tai nghe", "Khung": "Nhựa", "Đệm tai bằng memory foam": "Có", "Loại micro khử ồn": "Có thể tháo rời", "Thời lượng pin": "Chế độ analog 3.5mm: 10 giờ", "Phụ kiện": "Túi vải", "Cáp nối dài cho PC": "Không có"}',
        'Trải nghiệm âm thanh đỉnh cao chưa từng có với Tai nghe HyperX Cloud Orbit S, một sản phẩm được thiết kế để định nghĩa lại trải nghiệm chơi game và giải trí của bạn. Được trang bị củ loa từ phẳng Audeze™ 100mm huyền thoại, Cloud Orbit S mang đến chất lượng âm thanh đẳng cấp audiophile, chi tiết đáng kinh ngạc và dải tần siêu rộng, giúp bạn nghe rõ từng tiếng bước chân, tiếng nạp đạn hay bản nhạc nền hoành tráng với độ chính xác tuyệt đối. Điểm nhấn đột phá là công nghệ âm thanh 3D Waves Nx® với tính năng theo dõi đầu tiên tiến. Công nghệ này tạo ra một trường âm thanh ba chiều ổn định, như thể âm thanh đến từ các vị trí cố định trong không gian thực, mang lại trải nghiệm đắm chìm chưa từng thấy. Dù bạn quay đầu, âm thanh vẫn giữ nguyên vị trí, tạo cảm giác bạn đang thực sự ở trong thế giới game hoặc rạp chiếu phim tại gia. Với khả năng kết nối linh hoạt qua cổng USB Type-C, USB Type-A và jack 3.5mm, Cloud Orbit S tương thích hoàn hảo với PC, PS4, Xbox One, Nintendo Switch và các thiết bị di động. Microphone khử ồn có thể tháo rời đảm bảo giao tiếp rõ ràng với đồng đội. Đệm tai bằng mút hoạt tính bọc giả da cao cấp mang lại sự thoải mái tối đa ngay cả trong những phiên chơi game marathon. HyperX Cloud Orbit S không chỉ là một chiếc tai nghe, mà là cánh cửa dẫn bạn đến một thế giới âm thanh sống động, chân thực và đầy kịch tính.',
        1, 'https://product.hstatic.net/200000722513/product/orbit-s_2e06ed76ef1142c6af0ed3dea2fd8555_36dbfa032e65400daf1b3bfb70c61bfa.jpg'
    );
