INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Tai nghe Logitech G535 LIGHTSPEED Wireless Black', 'tai-nghe-logitech-g535-lightspeed-wireless-black', 13,
        '{"Thương hiệu": "Logitech", "Bảo hành": "24 tháng", "Series/Model": "G535", "Màu sắc": "Đen", "Thời lượng pin": "Có thể lên đến 33 giờ", "Kiểu tai nghe": "Over-ear", "Trọng lượng": "236g", "Drivers": "40 mm", "Kết nối": "Không dây LIGHTSPEED USB receiver", "Độ nhạy": "87.5 dB SPL/mW", "Microphone": "Có", "Trở kháng": "36 Ohms", "Tần số": "20Hz-20KHz", "Phạm vi kết nối không dây": "12 m", "Tương thích": "Windows 10 và macOS X 10.14"}',
        'Tai nghe Logitech G535 LIGHTSPEED Wireless Black là lựa chọn hoàn hảo cho game thủ yêu cầu sự tự do di chuyển và chất lượng âm thanh đỉnh cao. Với công nghệ không dây LIGHTSPEED tiên tiến, bạn sẽ trải nghiệm độ trễ siêu thấp, kết nối ổn định và phạm vi hoạt động lên đến 20 mét, cho phép bạn đắm chìm hoàn toàn vào thế giới game mà không bị gián đoạn. Trọng lượng siêu nhẹ chỉ 236 gram cùng thiết kế băng đô có thể điều chỉnh và đệm tai bằng vải thoáng khí mang lại sự thoải mái tối đa ngay cả trong những phiên chơi game kéo dài. Micro có thể lật lên để tắt tiếng và điều chỉnh vị trí dễ dàng, đảm bảo giao tiếp rõ ràng với đồng đội. Màng loa lớn 40mm mang đến âm thanh sống động, chi tiết và cân bằng, giúp bạn nghe rõ từng bước chân, tiếng súng hay mọi âm thanh quan trọng trong game. Thời lượng pin lên đến 33 giờ chơi liên tục chỉ với một lần sạc, bạn có thể thoải mái chiến đấu suốt ngày đêm. Tương thích đa nền tảng với PC, PlayStation, Nintendo Switch và các thiết bị di động khác thông qua bộ thu USB LIGHTSPEED, mang đến sự linh hoạt tối đa. Tai nghe còn tích hợp các nút điều khiển âm lượng và tắt tiếng ngay trên củ tai, giúp bạn dễ dàng tùy chỉnh mà không cần rời khỏi trận đấu. Thiết kế màu đen mạnh mẽ, hiện đại, phù hợp với mọi dàn PC gaming. Logitech G535 LIGHTSPEED Wireless Black không chỉ là một chiếc tai nghe, mà còn là vũ khí bí mật giúp bạn chinh phục mọi chiến trường ảo.',
        1, 'https://product.hstatic.net/200000722513/product/tech-g535-lightspeed-wireless-black-1_4185ee7505e341c3a8ad51b2c87009b1_5190e6a3ab164f1cbdc92e83d9f7d68e.png'
    );
