INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Ổ Cứng HDD Enterprise Western Digital Ultrastar DC HC520 12TB (HUH721212ALE604)', 'o-cung-hdd-enterprise-western-digital-ultrastar-dc-hc520-12tb-huh721212ale604', 32,
        '{"Nhà sản xuất": "Western Digital", "Bảo hành": "60 tháng", "Chuẩn giao tiếp": "SATA III", "Kích thước": "3.5 inch", "Giao diện": "SATA 6 Gb/s", "Dung lượng": "12 TB", "Cache": "256 MB", "RPM": "7200", "MTBF (M hours)": "2,000,000", "Chu kỳ tải/dỡ tải": "600,000"}',
        'Ổ cứng HDD Enterprise Western Digital Ultrastar DC HC520 12TB (HUH721212ALE604) là một giải pháp lưu trữ dung lượng cao, hiệu suất vượt trội dành cho các doanh nghiệp và trung tâm dữ liệu. Với dung lượng lên tới 12TB, ổ cứng này cung cấp không gian lưu trữ khổng lồ cho các ứng dụng đòi hỏi khắt khe, bao gồm lưu trữ dữ liệu lớn, ảo hóa, kho dữ liệu, phân tích và các môi trường điện toán đám mây.\n\nDòng Ultrastar DC HC520 được thiết kế để hoạt động liên tục 24/7 với độ tin cậy và độ bền bỉ cao, giảm thiểu thời gian chết và đảm bảo tính sẵn sàng của dữ liệu. Công nghệ HelioSeal tiên tiến của Western Digital mang lại hiệu suất ổn định, tiêu thụ điện năng thấp và khả năng hoạt động trong môi trường nhiệt độ cao.\n\nTốc độ quay 7200 RPM cùng với bộ nhớ đệm lớn giúp tối ưu hóa tốc độ truy xuất và ghi dữ liệu, đáp ứng nhu cầu xử lý khối lượng công việc lớn. Giao diện SATA 6Gb/s đảm bảo khả năng tương thích rộng rãi và tốc độ truyền dữ liệu nhanh chóng.\n\nĐược xây dựng với các thành phần chất lượng cao và trải qua quy trình kiểm tra nghiêm ngặt, WD Ultrastar DC HC520 12TB mang đến sự an tâm về độ tin cậy và hiệu suất lâu dài cho các hệ thống doanh nghiệp. Đây là lựa chọn lý tưởng cho các máy chủ, hệ thống lưu trữ mạng (NAS), hệ thống sao lưu và bất kỳ ứng dụng nào cần khả năng lưu trữ mạnh mẽ, đáng tin cậy và dung lượng lớn.',
        1, 'https://product.hstatic.net/200000722513/product/gearvn-o-cung-hdd-enterprise-western-digital-ultrastar-dc-hc520-12tb-1_cc3b0f4f00dc43029ffc3b1c4d578541.png'
