INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Ổ Cứng HDD Seagate Barracuda 4TB', 'hdd-seagate-barracuda-4tb-5400rpm', 33,
        '{"Sản phẩm": "Ổ cứng", "Hãng sản xuất": "Seagate", "Model (P/N)": "ST4000DM004", "Loại ổ": "HDD", "Dung lượng": "4TB", "Tốc độ quay": "5400rpm", "Bộ nhớ Cache": "64MB", "Chuẩn giao tiếp": "SATA3", "Kích thước": "3.5Inch"}',
        'Ổ Cứng HDD Seagate Barracuda 4TB (ST4000DM004) được lắp ráp chắc chắn theo tiêu chuẩn định dạng (Form Factor) 3.5” với kích thước chi tiết bao gồm chiều dài 146.99 mm, chiều rộng 101.85 mm và bề dày 20.20 mm.\n\nBề mặt bên ngoài của Ổ Cứng HDD Seagate Barracuda 4TB (ST4000DM004) được chia làm 3 phần riêng biệt: A, B và C. Phần A (nắp ổ) được làm từ kim loại đúc cứng cáp, hiển thị đầy đủ thông số kỹ thuật. Phần B (khung viền ổ cứng) được làm từ nhựa cứng siêu dày, có các điểm bắt ốc với lõi thép. Phần C là bề mặt kết nối tín hiệu, thường nằm ở dưới đáy ổ cứng. Bạn sẽ thấy một phần bảng mạch lộ ra với 2 cổng cắm kết nối chính; bảng mạch kết nối đầy đủ được đặt bên trong.\n\nỔ Cứng HDD Seagate Barracuda 4TB (ST4000DM004) có thể kết nối với nhiều thiết bị phổ biến như Laptop, Desktop, PC hay Smartphone thông qua 2 cổng cắm thông dụng (Data SATA và Power SATA). Tùy thuộc vào phụ kiện hỗ trợ, bạn có thể kết nối ổ cứng này với nhiều loại thiết bị khác nhau.\n\nVới dung lượng lưu trữ cực lớn lên đến 4 TB (tương đương gần 4000 GB), bạn có thể sử dụng Ổ Cứng HDD Seagate Barracuda 4TB (ST4000DM004) để lưu trữ đa dạng dữ liệu từ nhỏ đến lớn, bao gồm file, thư mục, hình ảnh, nhạc, phim, ứng dụng, phần mềm và nhiều dữ liệu quan trọng khác. Hơn nữa, bạn có thể chia ổ cứng thành 2, 3, 4 hoặc nhiều phân vùng khác nhau để quản lý và bảo mật dữ liệu khi cần thiết.\n\n*Lưu ý: Dù bạn chia ổ cứng thành bao nhiêu phân vùng, không nên nhồi nhét quá nhiều dữ liệu vào một phân vùng đến mức báo động màu đỏ. Tình trạng này có thể làm giảm băng thông truyền tải dữ liệu, dẫn đến việc khởi động chậm, đọc/ghi chậm và tải dữ liệu rất chậm. Do đó, hãy luôn giữ cho dung lượng trống của ổ cứng lớn hơn 10%.',
        1, 'https://product.hstatic.net/200000722513/product/hdd_seagate_baracuda_4tb_gearvn00_efd02c4f0c3844d1a9fa38f83c59e11c_e2bd8b7b318d4cfabf8a7421e3a4262a.jpg'
    );
