INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Ổ Cứng HDD Seagate IronWolf Pro 8TB', 'hdd-seagate-ironwolf-pro-8tb-7200rpm', 33,
        '{"Dung lượng": "8TB", "Tốc độ vòng quay": "7200 rpm", "Kích thước": "3.5 inch", "Kết nối": "SATA III 6Gb/s", "Bộ nhớ đệm (cache)": "128MB"}',
        'Ổ cứng HDD Seagate IronWolf Pro 8TB là giải pháp lưu trữ hiệu suất cao, dung lượng lớn, được thiết kế đặc biệt cho các môi trường NAS (Network Attached Storage) doanh nghiệp và chuyên nghiệp. Với dung lượng 8TB, ổ cứng này cung cấp không gian lưu trữ dồi dào, lý tưởng cho việc sao lưu dữ liệu quan trọng, lưu trữ media dung lượng lớn và chạy các ứng dụng yêu cầu băng thông cao. Seagate IronWolf Pro nổi bật với công nghệ AgileArray, tối ưu hóa hiệu suất cho các hệ thống RAID, cải thiện khả năng truy cập dữ liệu đồng thời và giảm rung động trong các thiết bị nhiều khay. Thiết kế bền bỉ với công nghệ Rotational Vibration (RV) Sensors giúp duy trì hiệu suất ổn định ngay cả trong các môi trường hoạt động liên tục 24/7 và chịu tải nặng. Tốc độ quay 7200 RPM cùng với bộ nhớ cache lớn đảm bảo tốc độ đọc/ghi dữ liệu nhanh chóng, giảm thiểu thời gian chờ đợi. Ổ cứng này còn tích hợp phần mềm Seagate Rescue Data Recovery Services, cung cấp dịch vụ phục hồi dữ liệu đáng tin cậy trong trường hợp xảy ra sự cố ngoài ý muốn. Với tuổi thọ MTBF lên đến 1 triệu giờ và được thiết kế để hỗ trợ lên đến 300TB mỗi năm, Seagate IronWolf Pro 8TB là lựa chọn an toàn, hiệu quả và đáng tin cậy cho mọi nhu cầu lưu trữ chuyên nghiệp.',
        1, 'https://product.hstatic.net/200000722513/product/hdd_seagate_ironwolf_8tb_gearvn00_356fda9588be4753aea0286830244006_01d38da23cda4ee188d84cd11e6a6516.jpg'
    );
