INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Ổ Cứng HDD WD 1TB Blue', 'wd-hdd-1tb-blue-7200rpm', 32,
        '{"Nhà sản xuất": "Western Digital", "Bảo hành": "24 tháng", "Chuẩn giao tiếp": "SATA III", "Kích thước": "25.4 x 147 x 101.6 mm (Cao x Dài x Rộng)", "Dung lượng": "1 TB", "Cache": "64 MB", "RPM": "7200 rpm", "Tốc độ đọc": "~100 MB/s", "Tốc độ ghi": "~100 MB/s", "Trọng lượng": "0.45 kg"}',
        'Ổ cứng HDD WD 1TB Blue là lựa chọn lý tưởng cho người dùng cần dung lượng lưu trữ lớn với chi phí hợp lý. Với dung lượng 1TB, bạn có thể thoải mái lưu trữ hàng ngàn bức ảnh, video chất lượng cao, bộ sưu tập nhạc đồ sộ, các tệp tin công việc quan trọng và nhiều ứng dụng, trò chơi khác. Ổ cứng này thuộc dòng WD Blue danh tiếng, nổi bật với độ tin cậy và hiệu suất ổn định, được thiết kế cho việc sử dụng hàng ngày, từ máy tính cá nhân, máy tính văn phòng cho đến các hệ thống lưu trữ mạng (NAS) dung lượng nhỏ. Tốc độ quay 5400 RPM cùng công nghệ IntelliSeek giúp tối ưu hóa việc tìm kiếm dữ liệu, giảm tiếng ồn và tiết kiệm năng lượng. Công nghệ NoTouch™ Ramp Load đảm bảo đầu đọc không chạm vào bề mặt đĩa khi không hoạt động, giúp giảm hao mòn và tăng tuổi thọ cho ổ cứng. Giao tiếp SATA III 6Gb/s mang lại tốc độ truyền dữ liệu nhanh chóng, tương thích với hầu hết các bo mạch chủ hiện đại. Kích thước 3.5 inch tiêu chuẩn giúp dễ dàng lắp đặt vào các thùng máy tính để bàn. WD 1TB Blue không chỉ cung cấp không gian lưu trữ dồi dào mà còn là một giải pháp đáng tin cậy, bền bỉ, đáp ứng mọi nhu cầu lưu trữ cơ bản của người dùng cá nhân và doanh nghiệp nhỏ.',
