INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Ổ Cứng HDD WD 2TB Black', 'wd-hdd-2tb-black-7200rpm', 32,
        '{"Nhà sản xuất": "Western Digital", "Bảo hành": "60 tháng", "Chuẩn giao tiếp": "SATA III", "Kích thước": "26.1 x 147 x 101.6 mm (Cao x Dài x Rộng)", "Dung lượng": "2 TB", "Cache": "64 MB Cache", "RPM": "7200 rpm", "Tốc độ đọc": "~100 MB/s", "Tốc độ ghi": "~100 MB/s", "Trọng lượng": "0.60 kg"}',
        'Cho dù bạn là một nhiếp ảnh gia, biên tập video, nghệ sĩ kỹ thuật số hay muốn xây dựng một hệ thống của riêng mình, WD Black chính là giải pháp dành cho bạn. Ổ đĩa WD Black mang lại hiệu suất tuyệt vời trong việc lưu trữ các tệp đa phương tiện lớn như ảnh, video và các ứng dụng nặng.\n\n**Dành cho các game thủ**\nNgoài hiệu năng hoàn hảo để chơi game, các ổ WD Black cung cấp dung lượng lưu trữ tuyệt vời cho các thư viện game ngày càng mở rộng của bạn, giúp bạn không phải lo ngại về các game offline bom tấn với đồ họa và dữ liệu khổng lồ. WD Black kết hợp với SSD sẽ mang lại hiệu suất tối đa cho PC của bạn.\n\n**Truyền dữ liệu tốc độ cao**\nVới 256MB bộ nhớ cache trên các mẫu 4TB và 6TB, công nghệ bộ đệm động của WD cải thiện hiệu suất trong thời gian thực để phân bổ và tối ưu hóa bộ đệm giữa hoạt động đọc và ghi.\n\n**Xử lý lõi kép**\nỔ đĩa WD Black có bộ xử lý lõi kép cung cấp khả năng xử lý gấp đôi so với bộ xử lý lõi đơn tiêu chuẩn để tối đa hóa hiệu suất ổ đĩa, đặc biệt là khi tải các trò chơi hoặc tệp đa phương tiện lớn.\n\n**Tăng độ tin cậy**\nCác ổ WD Black được trang bị Công nghệ StableTrac (từ mẫu 2TB trở lên). Điều này đảm bảo trục động cơ bên trong ổ cứng, giúp tăng độ tin cậy và giảm tác động do rung động từ các thành phần khác trong hệ thống.',
        1, 'https://product.hstatic.net/200000722513/product/hdd_wd_2tb_black_376ecdc3d4ab4616a27cdb28c92b9025_2e0c583d482e441982b77d7c3ba76bc2.jpg'
    );
