INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Ổ Cứng HDD WD 2TB Blue 7200RPM', 'hdd-wd-blue-2tb-7200rpm', 32,
        '{"Thương hiệu": "Western Digital", "Tên sản phẩm": "HDD WD Blue 2TB 7200rpm", "Bảo hành": "24 tháng", "Giao thức kết nối": "SATA3 - 3.5\"", "Dung lượng": "2 TB", "Tốc độ quay": "7200 rpm", "Kích thước": "147 x 101.6 x 26.1 mm", "Kiểu dáng": "3.5\"", "Tốc độ đọc": "Lên tới 215 MB/s", "Tốc độ ghi": "Lên tới 215 MB/s", "Trọng lượng": "0.45 kg"}',
        'Ổ Cứng HDD WD 2TB Blue 7200RPM là một giải pháp lưu trữ đáng tin cậy và hiệu suất cao, lý tưởng cho mọi nhu cầu từ người dùng cá nhân đến các doanh nghiệp nhỏ. Với dung lượng 2TB, bạn có đủ không gian để lưu trữ hàng ngàn ảnh độ phân giải cao, video 4K, bộ sưu tập nhạc khổng lồ, tài liệu quan trọng và nhiều ứng dụng khác. Tốc độ quay 7200RPM mang lại hiệu suất vượt trội so với các ổ cứng 5400RPM, giúp truy xuất dữ liệu nhanh hơn, thời gian khởi động hệ điều hành và tải ứng dụng được rút ngắn đáng kể. Điều này đặc biệt quan trọng đối với những công việc đòi hỏi xử lý dữ liệu liên tục như chỉnh sửa video, chơi game hoặc chạy máy ảo.\n\nDòng WD Blue nổi tiếng với sự cân bằng giữa hiệu năng, độ bền và giá cả phải chăng. Ổ cứng này được thiết kế để hoạt động ổn định trong thời gian dài, với công nghệ tiên tiến giúp giảm thiểu tiếng ồn và rung động, mang lại trải nghiệm sử dụng êm ái và thoải mái. WD Blue cũng tích hợp các tính năng bảo vệ dữ liệu tiên tiến, giúp bạn yên tâm về sự an toàn của những thông tin quan trọng. Giao diện SATA III 6Gb/s đảm bảo tốc độ truyền dữ liệu tối đa, tương thích hoàn hảo với hầu hết các bo mạch chủ và hệ thống máy tính hiện đại. Dù bạn đang nâng cấp máy tính cũ, xây dựng một hệ thống mới hay đơn giản là cần thêm dung lượng lưu trữ, Ổ Cứng HDD WD 2TB Blue 7200RPM sẽ là một lựa chọn thông minh và hiệu quả.',
        1, 'https://product.hstatic.net/200000722513/product/gearvn-hdd-wd-blue-2tb-7200rpm-1_fa7b6220ded04738a7fca1ff18185232_25ae485065a74e5f9b86cdf04470409a.png'
    );
