INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Ổ Cứng HDD WD Red Plus 6TB 5400RPM', 'o-cung-hdd-wd-red-plus-6tb-5400rpm', 32,
        '{"Nhà sản xuất": "Western Digital", "Bảo hành": "24 tháng", "Chuẩn giao tiếp": "SATA III", "Kích thước": "25.4 x 147 x 101.6 mm (Cao x Dài x Rộng)", "Dung lượng": "6 TB", "Cache": "128 MB Cache", "RPM": "5400 rpm", "Trọng lượng": "0.45 kg"}',
        'Khám phá sự hoàn hảo trong lưu trữ dữ liệu dành riêng cho hệ thống NAS (Network Attached Storage) của bạn với Ổ Cứng HDD WD Red Plus 6TB 5400RPM. Được thiết kế chuyên biệt để hoạt động bền bỉ trong môi trường 24/7, WD Red Plus là giải pháp tối ưu cho những ai đang tìm kiếm sự tin cậy, hiệu suất ổn định và dung lượng lớn để quản lý dữ liệu một cách an toàn và hiệu quả.\n\nVới dung lượng ấn tượng 6TB, ổ cứng này cung cấp không gian lưu trữ rộng lớn, đủ sức chứa hàng ngàn bộ phim, vô số ảnh kỷ niệm, tài liệu quan trọng và dữ liệu doanh nghiệp, dễ dàng truy cập và chia sẻ mọi lúc mọi nơi. Tốc độ quay 5400RPM được tối ưu hóa đặc biệt cho NAS không chỉ giúp giảm đáng kể tiêu thụ năng lượng và nhiệt độ hoạt động, mà còn kéo dài tuổi thọ của ổ đĩa, đảm bảo sự ổn định và liên tục cho hệ thống của bạn mà không gây ra tiếng ồn khó chịu.\n\nĐiểm mạnh vượt trội của WD Red Plus nằm ở công nghệ NASware 3.0 tiên tiến. Công nghệ này nâng cao khả năng tương thích với hầu hết các hệ thống NAS phổ biến, cải thiện độ tin cậy trong các cấu hình RAID, tối ưu hóa quá trình khôi phục lỗi và quản lý năng lượng thông minh, giúp bảo vệ dữ liệu của bạn khỏi mọi rủi ro. Dù bạn đang xây dựng một máy chủ phương tiện truyền thông đa chức năng, một trung tâm sao lưu dữ liệu kinh doanh, hay một hệ thống lưu trữ đám mây cá nhân, WD Red Plus 6TB đều mang lại sự an tâm tuyệt đối về hiệu suất và tính toàn vẹn của dữ liệu.\n\nChọn Ổ Cứng HDD WD Red Plus 6TB 5400RPM để biến hệ thống NAS của bạn thành một pháo đài dữ liệu vững chắc, hoạt động không ngừng nghỉ và đáng tin cậy. Đầu tư vào sự an toàn và hiệu quả – đầu tư vào WD Red Plus.',
        1, 'https://product.hstatic.net/200000722513/product/-iii-128mb-cache-5400rpm-wd60efzx-c85c8373-881c-42e4-a1f7-34dc8b5f35a8_f78db0501c03449e871c9be8d73ff2c7.png'
    );
