INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Bàn phím MadLions có dây MAD 68 HE Basic Magnetic Black (NO LED)', 'ban-phim-madlions-co-day-mad-68-he-basic-magnetic-black-no-led', 20,
        '{"Màu sắc": "Black", "Layout": "68%", "Chất liệu": "ABS", "Switch": "Magnetic Amber", "Keycap": "PBT", "Plate": "Aluminum", "Cấu trúc": "Shell", "Response Time": "0.02~3.5ms", "Lực nhấn": "40g", "Polling rate": "8000Hz", "Độ trễ": "0.125ms", "Scan rate": "32K"}',
        'Bàn phím MadLions có dây MAD 68 HE Basic Magnetic Black (NO LED) là một lựa chọn tuyệt vời cho những ai tìm kiếm sự đơn giản, hiệu suất và độ bền bỉ. Với layout 68 phím nhỏ gọn, bàn phím này tối ưu hóa không gian làm việc, mang lại sự thoải mái và linh hoạt tối đa. Thiết kế tối giản không LED không chỉ giúp giảm chi phí mà còn tạo nên vẻ ngoài sang trọng, chuyên nghiệp, phù hợp với mọi môi trường làm việc, từ văn phòng đến gaming. Chất liệu keycap PBT cao cấp, được in bằng công nghệ doubleshot, đảm bảo ký tự không bị phai mờ theo thời gian, mang lại cảm giác gõ êm ái và ổn định. Công tắc từ tính HE (Hall Effect) độc đáo mang đến trải nghiệm gõ mượt mà, phản hồi nhanh nhạy và đặc biệt là độ bền vượt trội lên đến hàng chục triệu lượt nhấn, vượt xa các loại switch truyền thống. Khả năng tùy chỉnh độ nhạy (Rapid Trigger) cho phép người dùng điều chỉnh điểm kích hoạt phím, mang lại lợi thế cạnh tranh trong các trò chơi đòi hỏi tốc độ phản ứng cực nhanh. Kết nối có dây USB-C chắc chắn, đảm bảo tín hiệu ổn định và truyền dữ liệu nhanh chóng. MadLions MAD 68 HE Basic Magnetic Black (NO LED) là sự kết hợp hoàn hảo giữa công nghệ tiên tiến, thiết kế tinh tế và độ bền bỉ, đáp ứng mọi nhu cầu của người dùng khó tính nhất.',
        1, 'https://cdn.hstatic.net/products/200000722513/download_c7027d681654412489160d4389ce925b.png'
    );
